float shapeBase(vec2 p, float s, int mode){
    vec2 prevP = p;
    p*=10.;

    if(mode ==1){
        p = abs(p);
        p-=iTime*0.5;
    } else {
        p*=2.;
        p.x-=0.2;
        p.y+=iTime*1.;
    }

    vec2 id = floor(p);
    vec2 gv = fract(p)-0.5;

    float n = Hash21(id);

    float w = 0.1;
    if(n<0.5 || n>=0.8){
        float dir = (n>=0.8)?1.0:-1.0;
        gv*=Rot(radians(dir*45.0));
        if(mode ==1){
            gv.x = abs(gv.x);
        }
        gv.x-=0.355;
    } else {
        w = 0.135;
    }

    w*=s;
    float d = B(gv,vec2(w,1.));
    return d;
}

float centerItem(vec2 p){
    vec2 prevP = p;

    float d = shapeBase(p,1.,1);

    p = prevP;
    p*=Rot(radians(45.));
    float d2 = B(p,vec2(0.2));
    d = max(d2,d);

    d2 = abs(B(p,vec2(0.22)))-0.005;
    p = prevP;
    d2 = max(abs(p.x)-0.1,d2);
    d = min(d,d2);

    p*=Rot(radians(45.));
    d2 = abs(B(p,vec2(0.22)))-0.005;
    p = prevP;
    d2 = max(abs(p.y)-0.1,d2);
    d = min(d,d2);

    p*=Rot(radians(45.));
    d2 = abs(B(p,vec2(0.24)))-0.001;
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.2;
    p.y-=0.2;
    p*=Rot(radians(-225.));
    d2 = shapeBase(p,1.,0);

    d2 = max(abs(p.x)-0.02,d2);
    d2 = max(abs(p.y)-0.3,d2);
    p = prevP;
    d2 = max(-p.y,d2);

    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.2;
    p.y+=0.2;
    p*=Rot(radians(225.));
    d2 = shapeBase(p,1.,0);

    d2 = max(abs(p.x)-0.02,d2);
    d2 = max(abs(p.y)-0.3,d2);
    p = prevP;
    d2 = max(p.y,d2);
    d = min(d,d2);    


    return d;
}

float circleItem(vec2 p){
    vec2 prevP = p;
    p*=Rot(radians(iTime*-30.));
    p = DF(p,2.);
    p -= vec2(0.06);
    p*=Rot( radians(-45.+sin(iTime*2.)*-10.));

    p.x*=2.;
    float d = abs(Tri(p,vec2(0.025),radians(45.)))-0.002;
    p = prevP;
    float d2 = abs(length(p)-0.05)-0.002;
    d = min(d,d2);
    d2 = length(p)-0.02;
    d = min(d,d2);
    return d;
}

float circleItems(vec2 p){
    vec2 prevP = p;

    p.x = abs(p.x)-0.77;
    p.y = abs(p.y)-0.32;
    float d = circleItem(p);

