    p*=Rot(radians(-90.));
    d2 = abs(Tri(p,vec2(0.04),radians(45.)))-0.001;
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.63;
    p.y = abs(p.y)-0.32;    
    d2 = abs(length(p)-0.015)-0.001;
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.84;
    p.y = abs(p.y)-0.46;    
    d2 = abs(length(p)-0.025)-0.001;
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.77;
    p.y = abs(p.y)-0.46;    
    d2 = abs(length(p)-0.013)-0.001;
    d = min(d,d2);    

    p = prevP;
    p.x = abs(p.x)-0.7585;
    p.y = abs(p.y)-0.186;
    d2 = abs(B(p,vec2(0.04,0.006)))-0.001;
    d = min(d,d2);  

