}

void mainImage( out vec4 fragColor,  in vec2 fragCoord )
{
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 prevP = p;
    vec3 col = vec3(0.);

    float d = centerItem(p);
