vec2 map(vec3 p)
{   
    float rCorner = .001;

    p.x = abs(p.x);
    p = (60. + 4.*cos(.07*iTime)) * sin(p/dot(p,p));
    p = kif(p);

    vec2 m1 = rBox(p, vec3(.004, .85, 1.05), rCorner, 3.);
    vec2 m2 = rBox(vec3(p.x - .05, p.yz), vec3(.004, .85, 1.05), rCorner, 3.);
    vec2 m3 = rBox(vec3(p.x - .02, p.yz), vec3(.04, .8, 1.), rCorner, 2.);
    vec2 m4 = rBox(vec3(p.x - .2, p.yz), vec3(.03, .45, .55), .005, 1.);
    vec2 m5 = rBox(vec3(p.x - .025, p.y + .5, p.z), vec3(.05, .01, 1.05), rCorner, 2.);
    vec2 m6 = rBox(vec3(p.x - .025, p.y - .5, p.z), vec3(.05, .01, 1.05), rCorner, 2.);
    vec2 m7 = rBox(vec3(p.x - .025, p.yz), vec3(.05, .01, 1.05), rCorner, 2.);
    vec2 m8 = rBox(vec3(p.x - .3, p.yz), vec3(.005, .15, .1), rCorner, 3.);

    vec2 m = add(add(add(add(add(add(add(m1, m2), m3), m4), m5), m6), m7), m8);
    glo += .15 / (1.2 + m2.x*m2.x*m2.x);
    return m;
}

vec2 tr(vec3 ro, vec3 rd)
{
    float far = 25.;
	vec2 h,t= vec2(.01);
	for(int i = 0; i < 128; i++)
	{
		h = map(ro + rd*t.x);
		if(h.x < .01 || t.x > far)
			break;
		t.x += h.x * .9;
        t.y = h.y;
  	}
    if(t.x > far)
        t.y = 0.;
	return t;
}

