void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 ro = vec3(.75*cos(-.1*iTime),
                   .75*sin(-.1*iTime),
                   -4.2);
    float targDist = 8.;
	vec3 camTarget = vec3(ro.x + targDist*sin(.1*iTime),
                          ro.y,
                          ro.z + targDist*cos(.1*iTime));
	vec3 up = vec3(sin(iTime/10.), cos(iTime/10.), 0.);
	vec3 camDir = normalize(camTarget - ro);
	vec3 camRight = normalize(cross(up, ro));
	vec3 camUp = normalize(cross(camDir, camRight));
    vec3 lightPos = vec3(2.5, 2.5, -5.);
    vec3 lightPos2 = vec3(-2.5, -2.5, -5.);

	vec2 screenPos = -1. + 2. * gl_FragCoord.xy / iResolution.xy;
	screenPos.x *= iResolution.x / iResolution.y;

	vec2 eps = vec2(0., .05);
	vec3 rd = normalize(camRight*screenPos.x + camUp*screenPos.y + camDir);

	vec2 t = tr(ro, rd);

    vec3 colRot = vec3(.35*sin(.317*(iTime)),
                       cos(.151*(iTime)),
                       .35*cos(.227*(iTime))) + 1.2;

    vec3 hit = ro + rd*t.x;
    vec3 lightDir = normalize(lightPos - hit);
    vec3 lightDir2 = normalize(lightPos2 - hit);

    vec3 norm = normalize(map(hit).x - vec3(map(hit - eps.yxx).x,
                                            map(hit - eps.xyx).x,
                                            map(hit - eps.xxy).x));


    float diff = max(0., dot(lightDir, norm));
    float spec = pow(max(dot(rd, reflect(norm, lightDir)), 0.), 50.);
    float spec2 = pow(max(dot(rd, reflect(norm, lightDir)), 0.), 50.);
    float ao = clamp(map(t.x + norm*.01).x / .5, 0., 1.);

    vec3 col = vec3(.1);
    col *= .1 * ao;

    if(t.y == 1.)
        colRot = vec3(.18);
    else if(t.y == 3.)
        col += glo*.035*colRot;
    else
    {
        colRot = colRot.yzx;
        col += glo*.035*colRot;
    }    

    col += .3 * diff * colRot;
    col += 1. * spec * vec3(0., .7, 1.);
    col += 1. * spec2 * vec3(0., .7, 1.);

    fragColor = vec4(col, 1.);
}

