){
    vec2 o_delta = o_pix_cor_nor - o_target;
    float n_tau = 6.2831;
    float n_angle = atan(o_delta.x , o_delta.y);
    float n_leave = n_angle / (n_tau/n_leaves); 
    float n_angle_mod_norm = fract(n_leave);
    float n_dist_squared = o_delta.x * o_delta.x + o_delta.y * o_delta.y;
    float n_dist_sqrt_root = sqrt(n_dist_squared);
    float n_radius_outcircle = n_dist_sqrt_root;
