    float n_incircle_radius_regular_polygon = n_radius_outcircle * cos((n_tau/2.0)/n_leaves);
    //https://www.desmos.com/calculator/xeiqmbj1ee?lang=de
    float n_angle_mod_norm_linear_inout = abs(n_angle_mod_norm - 0.5) * -2.0 + 1.0;
    float n_angle_mod_norm_exp_inout = 
        pow((n_angle_mod_norm - 0.5), 2.0) * -4.0 + 1.0;

    float n_angle_mod_norm_special_inout = 
        pow(mod(n_angle_mod_norm+0.5, 1.0)-0.5, 2.0)*4.0;

   float n_angle_mod_norm_sin_inout = 
