   float n_angle_mod_norm_sin_mirrored_inout = 
        sin(n_tau * mod(n_angle_mod_norm+0.5, 1.0) * 0.5) *-1.0 + 1.0;     

    return 
        vec4(
            //n_radius_outcircle-((n_radius_outcircle-n_incircle_radius_regular_polygon)*n_angle_mod_norm_exp_inout), 
