            //n_radius_outcircle-((n_radius_outcircle-n_incircle_radius_regular_polygon)*n_angle_mod_norm_sin_inout),
            n_radius_outcircle-((n_radius_outcircle-n_incircle_radius_regular_polygon)*n_angle_mod_norm_sin_mirrored_inout), // allmost it!
            1.,1.,1.
        );

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_cor_nor = (fragCoord-0.5*iResolution.xy)/iResolution.y; 
    vec4 o_dist = vec4(1.0,1.0,1.0,1.0);

    float n_sides_max = 9.0;
    float n_sides_min = 3.0; 
    float n_sides_range = n_sides_max - n_sides_min;
    float n_dist_lwst = 1.0;
    for(
        float n_side_norm = (1.0/n_sides_max)*n_sides_min;
        n_side_norm < 1.0;
        n_side_norm += 1.0/n_sides_max
    ){
        float n_side = n_side_norm*n_sides_max;

        float n_x = (1.0 / n_sides_range)*(n_sides_max - n_side)-0.5;
        if(o_pix_cor_nor.x > n_x){
            o_dist = f_o_dist_flower(o_pix_cor_nor, vec2(n_x+( (1.0 / n_sides_range)/2.0),0.0), n_side);
        }
        if(o_dist.x<n_dist_lwst){n_dist_lwst=o_dist.x;}

    }

    fragColor = vec4(
        sin(n_dist_lwst*333.0)
    ); 

}

float seed = 0.; // initial seed, set to 0 to get a seed based on the current time & date

const float tau = 6.2831853;

vec3 GeneratePattern( float seed, vec2 uv, vec2 duvdx, vec2 duvdy )
{
    float r = 1.0;
    float a = seed*.1;
    float c = cos(a)*r;
    float s = sin(a)*r;
    float q = fract((seed*.2/tau)/3.)*3.;
    for ( int i=0; i<30; i++ )
    {
    	//uv = abs(uv);

        // higher period symmetry
        float t = atan(uv.x,uv.y);
		t *= q;
        t = abs(fract(t*.5+.5)*2.0-1.0);
        t /= q;
        //q = q+.001;
        uv = length(uv)*vec2(sin(t),cos(t));

        uv -= .7;
        uv = uv*c + s*uv.yx*vec2(1,-1);
    }

	// pixellate to make it look like tiles
//    uv = floor(uv*27.)/27.;
//    vec3 tap = textureLod( iChannel0, uv*vec2(1,-1)+.5, 0.0 ).rgb;

    vec3 tap = textureGrad( iChannel0, uv*vec2(1,-1)+.5, duvdx, duvdy ).rgb;

    // quantize the colour to get polygonal shapes
//    tap = floor(tap*6.999)/6.;

    vec3 col = .5+.5*sin(seed+vec3(13,17,23)*tap);

    // quantize the colour to get polygonal shapes
    float shades = 4.;
    col = floor(col*(shades+.999))/shades;

    return col;
}


void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
if ( seed == 0. )
    {
        float mouseX = (iMouse.x>0.) ? iMouse.x: iResolution.x ;
        // randomize based on timestamp
        vec4 date = vec4(floor(iDate));
        date.w /= 3600.; // randomize once per hour
        // can't do a dot product!?
        date *= vec4(372.*24.,31.*24.,24.,1.);
        seed = float( (date.x+date.y+date.z+date.w) * mod(float(iTime*(mouseX/iResolution.x)),26543.) )/exp2(32.);
        seed = -50.*seed;
    }

    vec3 ray = vec3( (fragCoord-iResolution.xy*.5)/iResolution.y, 1. );
    ray = normalize(ray);

    vec3 camK = normalize(vec3(3.*sin(iTime*.1),3.*cos(iTime*.0618),5));
    vec3 camI = normalize(cross(vec3(0,1,0),camK));
    vec3 camJ = cross(camK,camI);
    ray = ray.x*camI + ray.y*camJ + ray.z*camK;

    float t = 5./ray.z;

    vec2 uv = ray.xy*t;

    uv /= 3.;
    uv += vec2(.2,.1)*iTime;
    float _seed = seed + floor(uv.x) + 5.*floor(uv.y); // floor(iTime*.3+10.0);

    float size = 5.;
    vec2 wrapuv = (fract(uv)-.5)*size;
    uv *= size;

    fragColour.rgb = GeneratePattern( _seed, wrapuv, dFdx(uv), dFdy(uv) );
}

