float mapWorm(vec3 p, out vec2 id, float h) {
    return 1E10;
    vec2 inset = -(p.yz) * .3 * cos( +iTime + 5.*p.x) * .051*cos( +iTime + 5.*p.x);
    p.yz -= inset;
    id.x = p.x + iTime/2.;
    float s = .2;// + .02*cos( +iTime *10.+ 5.*p.x);
    p.z -= 5.;
    p.y += h - s;
    p.z += cos(p.x + iTime) * sin(iTime);
    float d = length(p.yz) - s;

    return d;
}

float mapstarfish(in vec3 pos, out vec2 id, float h) {
    pos.xz*=2.;
    float rep = 3.;
    vec3 mp = mod(pos.xxz, rep) - rep / 2.;
    mp.xz = rotate2d(.3) * mp.xz;
    vec2 l_id = hash22(floor(pos.xz/rep));
    id.x = l_id.x;
    if(id.x < .6)
    return 3.;
    return opExtrusionT(vec3(mp.x, mp.z, pos.y - 0.0 + h), .03); 
}

float mapMushroom(in vec3 pos, out vec2 id, out vec3 ep) {
    float rep = 3.9;
    pos.x+=1.;
    pos.z+=.1;
    pos.xz = rotate2d(-.2) * pos.xz;

    vec3 u = pos;
    vec2 m_id = hash2( 12.387*floor(pos.xz / rep));

    pos.xz = mod(pos.xz, rep) - rep / 2.;
    pos.xz = rotate2d(m_id.y*33.14) * pos.xz;
     pos *= 1.6;

//    pos.x -= m_id.x * 1.;

    vec3 pcap = pos;

    pcap.xy = rotate2d(-.5 ) * pcap.xy;
    pcap.x+=2.;

    float h= 1.9 + .6*cos(3.14*m_id.y+iTime/1.515) + m_id.x * 4.;
    id.x = pos.y / h;
    pos.y += 1.;
    float bcap = smoothstep(13.0, 2.01, pos.y);
    pos.x += cos(pos.y/1.5 - sin(iTime)  ) /2.;
    vec2 r = normalize(vec2(pos.z, pos.x) );
    float a = atan(r.x, r.y);
    if(m_id.x < .4 ||noise(floor(u / rep ) *vec3(1, .0, .1)) <.2)
     return 1.1;
    vec2 inset = -pos.xz * cos( a * 40. + iTime *0.)  * .5;
    pcap.xz+= inset / 8.;
    pos.xz+= inset/64.;
    float s = .95 - m_id.x * .8 + sin(m_id.y + iTime / 3.) / 10.;
    float scap = -.1;//-1.05  + sin(m_id.y+iTime / 3.);// + m_id.y/5.*sin(11.*length(pos.xz));
    id.x = 1. + sin(iTime / 3.);
    float j = 5.5;
    float j2 = 5.;
    float h2 = h  - .0 ;
    id.y = m_id.y;
     pcap.y -= h;


     float prep = 4.;
 //   pcap.y = mod(pcap.y, prep) - prep / 2.;

    float dcap = length(pcap * vec3(1., j, 1.) - vec3(0., j*h*0., 0.)) - scap;
    ep = pos - vec3(0., j*h, 0.);
    float dt = max( length(pos.xz) - s, abs(pos.y) - h * 1.2) ;

//     dcap = opST(indcap, dcap);
    return smin(dcap, dt, 10.5);

}


float mapJellyfish(in vec3 pos) {

    float s = 5.;
    pos.z += 2.;
    float mrep = 3.5;
    if (pos.z > 11. || pos.y < -1.5 || pos.y > 4.)
         return 1e10; 
     ;//pos *= 2.;
     pos.xy = rotate2d(.2) * pos.xy;

    vec3 id = floor(pos / vec3(mrep));
    vec2 r = hash2(id.xz);
    float t = .5 * (iTime * (1.5 +  r.y / 2.));
    pos.y -= t / 1. - 0.1*((r.x+ cos(t * 6.)));

    pos.y += r.x * 10.;
 //   pos.x += r.y * 5.;
    pos = mod(pos, vec3(mrep)) - vec3(mrep/2.);
    pos.xz = rotate2d(iTime) * pos.xz;

    //pos.xz=rotate2d(5.) * pos.xz;
//       pos.z += 2.;
    float h = 2.5 / s;
    float st = .015 / s;
    float sc = 1.6 / s;
    float j = 1.5;

    float dc = length(pos * vec3(1., j, 1.) - vec3(0., h*j, 0)) - sc;

     float mtrep = .2;
    float l = 4. /  s;
    vec2 o = vec2(0.);//floor(pos.xz / mtrep);
    vec2 ct = pos.xz;
    ct.x += cos(pos.y * 2./h + iTime * 3. + o.x) / (s * 3.);
    ct.y += sin(pos.y * 2. / h + iTime * 3. + o.y) / ( s * 3.);

    vec2 mt = ct.xy;//-mtrep*clamp(round(pos.xz/mtrep),-l,l);
    mt = ct.xy-mtrep*clamp(round(ct.xy/mtrep),-l,l);

    float dt = max( length(mt) - st, abs(pos.y) - h);
    return smin(dt, dc, .4 / s);
}

vec2 map( in vec3 pos, out vec2 id, bool b, out vec3 ep )
{
    vec2 res = vec2( 1e10, 0.0f );
    float d = 1e10;
    id.y = 1.;
    float dg=1e10;
    float y = 1.0;
    float dm;
    vec2 tmp_id;

    if ((dm = mapMushroom(pos, id, ep)) < res.x) {    
        res.y = 7.0f;
        res.x = dm;// smin(d, dg+.9, 1.5);
    }
    y += noise(pos / 5.) / .5;
    if ((dg = abs(pos.y + .5 + y ) ) < res.x) {
        res.y = 3.0f;
        res.x = dg;//smin(dg, dm, .5);
    }
    res.x = smin(dg, dm, .2);       
    if ((d = abs(pos.y - 10.5 + 1.*getwaves(pos.xz / 5., iTime) ))  < res.x) {
        res.y = 2.0f;
        res.x = d;
    }
    if ((d = mapJellyfish(pos)) < res.x) {
        res.y = 5.0f;
        res.x = d;
    }
    if ((d = mapstarfish(pos, tmp_id, y + .5)) < res.x) {    
        res.y = 8.0f;
        res.x = d;
        id = tmp_id;
    }
    if ((d = mapWorm(pos, tmp_id, y + .5)) < res.x) {    
        res.y = 9.0f;
        res.x = d;
        id = tmp_id;
    }    
    return res;
}

const float maxHei = 0.8;

vec3 raymarch(vec3 ro, vec3 rd) {

    vec3 acc = vec3(0.);
    float t = 1.;
    float far = 40.;
    float num_step = 164.;
    float stepSize = (far - 1.) / num_step;

    for(int i = 0; float(i) < num_step; i++) {
        vec3 pos = ro + rd * t;

        vec3 pc = pos + vec3(-.2, 0.0, .0) * (abs(pos.y + 20.));
        float v = (texture(iChannel0, pc.xz / 190. + .5).x);
       // v = pow(v / 5., .5);
        v = smoothstep(.4, .1, v);
        acc += vec3(v) / num_step / max(5.0, pow( (pos.y * 1.), 1.) );
        t+= stepSize;
    }
    return acc;
}

vec2 castRay( in vec3 ro, in vec3 rd, out vec2 id, out vec3 ep )
{
    vec2 res = vec2(-1.0,-1.0);

    bool b = false;

    float tmin = 1.0;
    float tmax = 35.0;
    // raymarch primitives   
    {

        float t = tmin;
        for( int i=0; i<250 && t<tmax; i++ )
        {
            vec2 h = map( ro+rd*t,id, b, ep );
            if( abs(h.x)<(0.001*t) )
            { 
                     res = vec2(t,h.y);
                     break;
            }
            t += h.x * .5;
        }
    }
    return res;
}



mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 normal (in vec3 p)
{
    vec2 e = vec2(.001, .0);
    vec2 id;
    vec3 ep;
    bool b = false;
    float d = map (p,id,b, ep).x;
    vec3 n = vec3 (map (p + e.xyy,id,b, ep).x - d,
                   map (p + e.yxy,id,b, ep).x - d,
                   map (p + e.yyx,id,b, ep).x - d);
    return normalize(n);
}
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    vec2 id;
    bool d = false;
    float res = 1.0;
    float t = mint;
    vec3 ep;
    for( int i=ZERO; i<16; i++ )
    {
		float h = map( ro + rd*t,id, d,ep ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 render( in vec3 ro, in vec3 rd )
{ 

    vec3 ep;
    vec2 id;
    vec2 res = castRay(ro,rd, id, ep);
    float t = res.x;
	float m = res.y;
    vec3 k_water = 2.5*vec3(0.01, .1, .35);
    vec3 k_sun = 2.*vec3(.5, .2, .1 ) + k_water / 2.;
    vec3 k_backlit = vec3(2., 3.4, .5) * 2.;
    vec3 lbackdir = normalize( -vec3(0., 1, 0.) );
    vec3 ldir = normalize( -vec3(-30., -100, 10.) );
    vec3 col = vec3(0.);//k_water;
    vec3 p = ro + rd * t;
    vec3 nor = normal(p);
    vec3 albedo = vec3(.2);
    float ndotl = max(0.2, dot(nor, ldir) );
    float ndotlback = max(.0, dot(nor, lbackdir) );
    float d = length(ro - p);

  if (res.x > .0f) {
    	vec3 c = vec3((texture(iChannel0, p.xz / 25. + .5).x)) * vec3(0.1, 0.25, 0.55);
         col += smoothstep(0.05, 0.9, pow(c / 2., vec3(2.2)) / .02) / 2.;
         col += k_water / 3.;
    }else {
        d = 100.;
    }

    if (res.y == 5.) {    
        albedo = vec3(0.,.2,.2)/15.;
        col+=.4;
    }

    if (res.y == 3.) {
        vec3 k_sand = vec3(.5, .5, .1);
        float shadow = calcSoftshadow( p, -ldir, .5, 10. );
        vec3 t = k_sand * texture(iChannel1, p.xz * 1.).xxx * 15.;
        col += t / 8.;
        //col+=vec3(.2);
        col = col*.3*shadow + col *.7; 
    }

    if (res.y == 7.) {
        albedo = vec3(.15);
        vec3 c1 = vec3(.2, .1, .1);
        vec3 c4 = pal(id.y/1. + .1,vec3(.00,0.0,.28),vec3(.2,.3,-.16),vec3(0.33,0.36,0.73),vec3(0.74,1.00,0.79));
        col = .0+ c1/200.;
        albedo = c4 / 5.;
        float up = smoothstep(.3, .4, nor.y);
        //albedo *= vec3( pow( 18./length(ep), 6.5) );
        col += texture(iChannel2,  p.xz).rrr * up;

    }
    if (res.y == 8.) {

        col += .7 * pal(id.x,vec3(0.95,0.65,0.00),vec3(0.26,0.47,0.00),vec3(0.64,0.57,0.00),vec3(0.00,0.00,0.00));
        albedo = col;
    }
    if(id.y == 4.) {
    //    col = vec3(.0);
        albedo += vec3(.2);
    }
    if (res.y != 2.) {
        col += albedo * ndotl * k_sun;
     } else {
         col += max(0., smoothstep(.9, 1., -nor.y) ) * vec3(.3, .5, .25) / 2.;
         col /= 1.3;

     //    col += vec3(.1,.1,.15) * 2.;
     }
     if (res.y == 9.) {
         albedo = vec3(.3 + .05 * sin(10.* id.x))  * vec3(.44, .4, .1) * 3.;
     //    albedo *= texture(iChannel1, p.xy * 2.).xxx * 2.;
         col = albedo ;
     }
    //if (res.y == 6.0f) 

    //col+= .1;
        if(res.y!=2. && res.y != 9.) {
        col += albedo * ndotlback * k_backlit * 2.;
    }

    col = mix(col, k_water * 1.8, clamp(pow(d / 33., 2.), 0., 1.  ) );


    col += mix( vec3(0.), vec3(1.) ,max(0., rd.y / 1.5));

    vec3 r= raymarch(ro, rd) * 8.;
    col += r;
    float turfu = 0.;//pow(sin(iTime*8.), 2.);
    r *= mix(vec3(1.), vec3(1., .3, .3), turfu);
    col = mix( col, col * ((r) * 1.), turfu * .8); 
   	return vec3(col);
}

float ghash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

float bubble(vec2 uv) {
    uv.x -= iTime;
    // uv.y -= iTime;
    uv *= 40.;

    float acc = 0.;
    for (int i = 0; i < 3; ++i) {
        float rep = 2.;
        vec2 f= floor(uv / rep);
        vec2 ouv = uv;

        //ouv.x += mod(f.y, 2.);
         float r = ghash(f / float(21.*float(1+i)));

        vec2 p = mod(ouv, rep) - rep/2.;
   // p.y += sin(4.*iTime) * r/2.;
        float c = smoothstep(.9, .8, length(p) );
        c *= smoothstep(.3, .75, length(p));
        if(r < .97)
            c = 0.;
        acc+= c;
        uv *= 2.;
    }
    return acc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = .0f; //iTime;

    vec3 ro = vec3( sin(0. / 2.), -.3f ,  -6.0f+ cos(0. / 2.));
    vec3 ta = vec3( 1., 1.9, 0. );
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    // ray direction
    vec3 rd = normalize( ca * vec3(p.xy,1.4) );
    ro.x += 2.;// + iTime;
   // ro.z += iTime*5.;
    ro.y -=1.;
    // render	
    vec3 col = render( ro, rd );

    // gamma
    col = pow( col*1. * (.95 + .05*bubble(p.yx + vec2(0., 0.))), vec3(2.5 ));

    tot += col;
    fragColor = vec4( tot, 1.0 );
}

