    return fragCoord.x > iResolution.x - 1.
         || fragCoord.x < 1.
         || fragCoord.y > iResolution.y - 1.
         || fragCoord.y < 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 starCoord = fragCoord / 500.;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
