vec3 effect(vec2 p, vec2 pp) {
  float aa = 2.0/RESOLUTION.y;

  float d = df(p);
  const float hoff = 0.685;
  const float inte = 0.85;
  const vec3 bcol0 = HSV2RGB(vec3(0.50+hoff, 0.85, inte*0.85));
