    float topMask = step(0.7, uv.y);
    float bottomMask = step(uv.y, 0.3);
    float leftMask = midSegment * step(uv.x, 0.3);
    float rightMask = midSegment * step(0.3, uv.x);


    float top = (2.0 * mod(atan(uv.y - 0.7, uv.x - 0.3) / TAU, 1.0)) * topMask;
    float bottom = (2.0 * mod((PI + atan(uv.y - 0.3, uv.x - 0.3)) / TAU, 1.0)) * bottomMask;
    float left = (uv.y * -2.5 + 1.75) * leftMask;
    float right = (uv.y * 2.5 - 0.75) * rightMask;

    float route = right * 0.25 + top * 0.25 + topMask * 0.25 + left * 0.25 + leftMask * 0.5 + bottom * 0.25 + bottomMask * 0.75;

    col = vec3(mod(route - iTime, 1.0));

    col = mix(vec3(0.0), col, smoothstep(0.2, 0.2 + EDGE, d));
    col = mix(vec3(0.0), col, smoothstep(0.3, 0.3 - EDGE, d));

    fragColor = vec4(col, 1.0);
}

#define PI 3.1415926

vec3 Grid(in vec2 uv) //
{
    vec2 cell = floor(mod(uv,2.)); //
    vec3 res = vec3(0.4);

    if(cell.x == cell.y) res = vec3(0.6);

    //fwidth(uv.x)
    res = mix(res,vec3(0),smoothstep(1.1*fwidth(uv.x),fwidth(uv.x),abs(uv.x)));//y
    res = mix(res,vec3(0),smoothstep(1.1*fwidth(uv.y),fwidth(uv.y),abs(uv.y)));//x

    return res;
}

vec2 fixUV(vec2 fragCoord)
{
    return 3.*(2.*fragCoord - iResolution.xy) / min(iResolution.x,iResolution.y);
}

float Function(float x)  //
{
    vec2 m = fixUV(iMouse.xy);
    float T = 4.+2.*cos(iTime);
    if(iMouse.z>0.1)
        T += m.x;

    //return smoothstep(1.,0.,x);
    //return mod(x,2.);
    return sin(2.*PI/T*x+iTime);
}

float Draw(vec2 uv)
{
    float fx = uv.x;//uvx
    float fy = Function(fx);//uvxy

    return smoothstep(fy-0.01,fy+0.01,uv.y);
}

#define AA 4
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv = fixUV(fragCoord);

    vec3 col = Grid(uv);

    float count = 0.;
    for(int m=0;m<AA;m++)
    {
        for(int n=0;n<AA;n++)
        {
            vec2 offset = (vec2(float(m),float(n))-0.5*float(AA))/float(AA)*2.;
            count += Draw(fixUV(fragCoord+offset));
        }
    }

    if(count>float(AA*AA)/2.)
        count = float(AA*AA)-count;
    count = 2.*count/float(AA*AA);

    col = mix(col,vec3(1.,1.,0.),count);

    //col = vec3(Draw(uv));

    fragColor = vec4(col,1.);
}

// Mesh resolution
#define RES vec3(0.5)

// Inigo Quilez's polynomial smooth minimum from https://iquilezles.org/articles/smin:
float smin(in float a, in float b, in float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float mapScene(in vec3 p) {
    float dist = length(p - vec3(-1.0, -1.0 + sin(iTime), 1.0)) - 1.5;
    dist = smin(dist, length(p - vec3(1.0, 1.0 + 0.5 * cos(iTime), -1.0)) - 2.0, 1.5);
    dist = smin(dist, length(p - vec3(-4.0, 1.0, 0.5)) - 2.0, 1.0);
    dist = smin(dist, length(p - vec3(-2.0, 1.0, -3.0)) - 1.0, 2.25);
    dist = smin(dist, length(p - vec3(3.0 + cos(iTime), -1.0, -1.0)) - 1.0, 1.0);
    return dist;
}

// Meshify!
float mapMesh(in vec3 p, out vec3 nor) {
    vec3 pos = floor(p / RES) * RES;
    vec3 uvw = (p - pos) / RES;

    // Determine the vertices of the tetrahedron we're in
    vec3 v0 = dot(uvw, vec3( 1,  1,  1)) > 2.0 ? vec3(1, 1, 1) : vec3(0, 0, 0);
    vec3 v1 = dot(uvw, vec3(-1, -1,  1)) > 0.0 ? vec3(0, 0, 1) : vec3(1, 1, 0);
    vec3 v2 = dot(uvw, vec3(-1,  1, -1)) > 0.0 ? vec3(0, 1, 0) : vec3(1, 0, 1);
    vec3 v3 = dot(uvw, vec3( 1, -1, -1)) > 0.0 ? vec3(1, 0, 0) : vec3(0, 1, 1);

    // Solve for barycentric coordinates
    mat4 map = inverse(transpose(mat4(v0, 1, v1, 1, v2, 1, v3, 1)));
    vec4 bary = vec4(uvw, 1) * map;

    // Calculate isovalues at the tetrahedron vertices
    vec4 isovals = vec4(
        mapScene(pos + v0 * RES),
        mapScene(pos + v1 * RES),
        mapScene(pos + v2 * RES),
        mapScene(pos + v3 * RES)
    );

    nor = normalize((map * isovals).xyz / RES); // Normalized gradient
    return dot(isovals, bary); // Interpolate isovalues
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 3.14;
    vec2 uv = (fragCoord - center) / iResolution.y;
    fragColor = vec4(0.24375, 0.16125, 0.07875, 1.0);

    // Default before interaction
    if (ivec2(iMouse) == ivec2(0)) mouse = vec2(-0.25, -0.5);

    // Calculate ray
    vec3 ro = vec3(0.0, 0.0, 10.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    // Rotate with mouse
    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);

    ro.yz *= mat2(cp, -sp, sp, cp);
    ro.xz *= mat2(cy, -sy, sy, cy);
    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    // Raymarch
    float t = 0.0;
    vec3 nor;
    for (int i=0; i < 200; i++) {
        vec3 p = ro + rd * t;
        float dist = mapMesh(p, nor);
        if (dist < 0.001) {
            fragColor.rgb = 0.5 + 0.5 * nor;
            break;
        }

        if (t > 25.0) {
            break;
        }

        t += dist;
    }
}

