vec4 f_o_dist_flower(
    vec2 o_pix_cor_nor,
    vec2 o_target, 
    float n_leaves
){
    vec2 o_delta = o_pix_cor_nor - o_target;
    float n_tau = 6.2831;
    float n_angle = atan(o_pix_cor_nor.x , o_pix_cor_nor.y);
    float n_leave = n_angle / (n_tau/n_leaves); 
    float n_angle_mod = mod(n_angle, n_tau/n_leaves);
    float n_angle_mod_norm = n_angle_mod / (n_tau/n_leaves);
    float n_angle_mod_norm_modhalf = mod(n_angle_mod_norm, 0.5);
    float n_angle_mod_norm_linear_in_out;
    if(n_angle_mod_norm > 0.5){
        n_angle_mod_norm_linear_in_out = 1.0 - (n_angle_mod_norm_modhalf*2.0);
    }else{
        n_angle_mod_norm_linear_in_out = (n_angle_mod_norm_modhalf*2.0);
    }

    float n_dist_squared = o_delta.x * o_delta.x + o_delta.y * o_delta.y;
    float n_dist_squared_modified = n_dist_squared *
        (1.0 - (n_angle_mod_norm_linear_in_out*-1.0));


    return 
        vec4(
            sqrt(n_dist_squared_modified),
            n_leave/n_leaves, 
            n_angle, 
            n_angle_mod_norm
        );

