    //do bending
    float z = time*24.0; //12.0
    vec3 pnt = vec3(sampleBendCurve(z), z); ////0.0 oc
    z -= 23.0;
    //time -= 2.0;
    vec3 dir = normalize(vec3(sampleBendCurve(z+NORMALEPS), z+NORMALEPS) - vec3(sampleBendCurve(z), z));
    //time += 2.0;

    //get camera ray and sample
    Camera cam = createCamera(pnt, dir, vec3(0.0, 1.0, 0.0));

    vec2 cRaySS = (fragCoord/iResolution.xy)*2.0-1.0;
    vec3 cRayWS = CS2WS(SS2CS(cRaySS, cam), cam);

    int steps;
    float dist = trace(cam.position, cRayWS, steps);

    vec3 col = vec3(0.0);
    if(dist < MAXDIST + time*4.0*1.0)
    {
        vec3 norm = getNormal(cam.position + cRayWS * dist);
        col = norm * 0.5 + 0.5;
    }
    else
    {
        float angFrac = acos(cRayWS.y)/PI;
        col = angFrac * vec3(0.3, 0.1, 0.8);
    }

    // Output
    return vec4(col, 1.0);
}

