    float iIter = 1.0 / float(iterSize);
    for(int x=0; x<iterSize; x++)
    {
        for(int y=0; y<iterSize; y++)
        {
            col += image(fragCoord + vec2(float(x) * iIter, float(y) * iIter));
        }
    }

    fragColor = col * iIter * iIter;
}

