///CONSTANTS
#define MAXSTEPS 100
#define MAXDIST 50.0
#define SDEPS 0.01
#define NORMALEPS 0.05 //.05
#define D2R 0.0174533
#define R2D 57.296
#define PI 3.14159265
#define IPI .318309886

///CAMERA STUFF START
struct Camera
{
    vec3 position;

    vec3 right;
    vec3 up;
    vec3 forward;

    float fovX;
    float fovY;

};

Camera createCamera(vec3 pos, vec3 forward, vec3 up)
{
    return Camera(pos, cross(up, forward), up, forward, 90.0*D2R, 60.0*D2R);
}
Camera createForwardCamera(vec3 pos, float theta)
{
    return Camera(pos, vec3(cos(theta),0.0,sin(theta)), vec3(0.0,1.0,0.0), vec3(cos(theta+PI*0.5),0.0,sin(theta+PI*0.5)), 90.0*D2R, 60.0*D2R);
}

vec3 SS2CS(vec2 raySS, float fovX, float fovY)
{
    float z = 1.0;
    return normalize(vec3(z*tan(fovX/2.0)*raySS.x, z*tan(fovY/2.0)*raySS.y, z));
}
vec3 SS2CS(vec2 raySS, Camera cam) {return SS2CS(raySS, cam.fovX, cam.fovY);}

vec3 CS2WS(vec3 rayCS, vec3 right, vec3 forward, vec3 up)
{
    return vec3(dot(rayCS, right), dot(rayCS, up), dot(rayCS, forward));
}
vec3 CS2WS(vec3 rayCS, Camera cam) { return CS2WS(rayCS, cam.right, cam.forward, cam.up); }

///SDF functions
float sdSphere(vec3 p, vec4 sphere)
{
    return MAXDIST;
    return length(p - sphere.xyz) - sphere.w;
}
float sampleFunc(float x)
{
    x = sqrt(PI*0.5*200.0 + pow(mod(x, sqrt(26.0*PI*200.0)),2.0));
    return sin(x*x*0.005);
}

//Hallway Stuff
float time;
float bendTheta;

vec2 sampleBendCurve(float z)
{
    z *= 0.1;

    float y = 1.*15.*(sin(z*PI*.5/15.)*0.5+0.5)*sin(time+z*0.2)*1.5+cos(z)*2.0*(sin(0.8*time)*0.5+0.5)*2.0+3.0;          
    float x = sin(z)*(sin(0.8*time)*0.5+0.5)*2.0+3.0;

    return .7*vec2(x, y);
}

float sdHallway(vec3 p, vec2 dimensions, vec3 right, float time)
{
    //p.z += time*24.0;

    vec3 origin = vec3(sampleBendCurve(p.z), p.z);
    vec3 forward = normalize(vec3(sampleBendCurve(p.z+NORMALEPS), p.z+NORMALEPS) - origin);

    float len = p.z;//*.5;//p.z*(.7-abs(sin(time))*.4); //use arc length later
    //float scaleFrom = p.z - 10.0;//p.z - mod(p.z, 100.0);
    //len = (p.z - scaleFrom) * (.7-(sin(time*0.4))*.4) + scaleFrom;

    vec3 up = cross(right, forward);
    right = cross(forward, up);

    p = p - origin;
    p = vec3(dot(p, right), dot(p, up), dot(p, forward));

    dimensions += sampleFunc(len); //mod(len, 100.0) for some reason looks cool and not broken?

    return min(min(dimensions.x - p.x, dimensions.x + p.x), min(dimensions.y - p.y, dimensions.y + p.y));
} 
float sdHallwayWorst(vec3 p, vec2 dimensions, vec3 right, float time)
{

    //used to just return here
    float dist1 = sdHallway(p, dimensions, right, time);

    //worst case dist

    float distReg = min(min(dimensions.x - p.x, dimensions.x + p.x), min(dimensions.y - p.y, dimensions.y + p.y));
    dimensions += min(distReg, 1.0);

    float y = 0.0;
    float xEps = mix(.2, 4., clamp((time - 0.0)/(40.0 - 0.0), 0.0, 1.0));//3.;
    float distWorst = sqrt(xEps*xEps);

    return min(distWorst, dist1);
} 

vec3 twistScene(vec3 p)
{
    //p.z += time*24.0;

    //rotation amount
    float len = p.z; //use arc length later
    float twistTheta = (len+time*4.0) * 0.05;

    //origin and rot axis
    //vec3 origin = vec3(0.0);
    //vec3 forward = vec3(0.0, 0.0, 1.0);
    vec3 origin = vec3(sampleBendCurve(p.z), p.z);
    vec3 forward = normalize(vec3(sampleBendCurve(p.z+NORMALEPS), p.z+NORMALEPS) - origin);

    //get axis' (don't matter as long as perp)
    vec3 arb1 = vec3(0.0, 1.0, 0.0); //arbitrary vec -> right and up
    vec3 arb2 = vec3(1.0, 0.0, 0.0);

    vec3 right = normalize(2.0 * normalize(cross(arb1, forward)) + 1.0 * normalize(cross(arb2, forward)));
    vec3 up = cross(forward, right);

    //convert to axis space
    p = vec3(dot(p - origin, right), dot(p - origin, up), dot(p - origin, forward));

    //rotate about z in axis space
    p = p.x * vec3(cos(twistTheta), sin(twistTheta), 0.0) + p.y * vec3(cos(twistTheta+PI*.5), sin(twistTheta+PI*.5), 0.0) + p.z * vec3(0.0, 0.0, 1.0);

    //convert back to world space
    p = p.x * right + p.y * up + p.z * forward + origin;

    //p.z -= time*24.0;

    return p;
}


///RAYMARCHING and SDF sampling


float sampleSDF(vec3 p)
{
    p = twistScene(p);
    return sdHallway(p, vec2(5.0, 8.0), vec3(1.0, 0.0, 0.0), time);
}
float sampleSDFWorst(vec3 p)
{
    p = twistScene(p);
    return sdHallwayWorst(p, vec2(5.0, 8.0), vec3(1.0, 0.0, 0.0), time);
}


vec3 getNormal(vec3 p)
{
    vec2 e = vec2(NORMALEPS*(1.0+4.0*smoothstep(80.0, 450.0, time)), 0.0);

    //this creates some other artifacts so if your computer doesn't have the ridging artifacts, turn this to 0
    float scaleBandaid = 0.0;
    vec3 scale = vec3(1.0) + scaleBandaid*normalize(vec3(sampleBendCurve(p.z+NORMALEPS), p.z+NORMALEPS) - vec3(sampleBendCurve(p.z), p.z));

    return normalize(vec3(
    (sampleSDF(p + e.xyy*scale.x) - sampleSDF(p - e.xyy*scale.x))/scale.x,
    (sampleSDF(p + e.yxy*scale.y) - sampleSDF(p - e.yxy*scale.y))/scale.y,
    (sampleSDF(p + e.yyx*scale.z) - sampleSDF(p - e.yyx*scale.z))/scale.z
    ));
}

float trace(vec3 ro, vec3 rd, out int steps) //all WS and rd is normalized
{
    float dist = 0.0;
    float sd;

    float startSign = sign(sampleSDF(ro));

    for(int i=0; i<MAXSTEPS; i++)
    {
        steps++;
        sd = sampleSDF(ro + rd*dist);
        dist += sd;

        if(abs(sd) <= SDEPS || dist >= MAXDIST + time*4.0*1.0 ) { return dist; }
    }

    return dist;
}

