    vec2 o_pixel_coord_norm = fragCoord.xy / iResolution.xy;

    o_pixel_coord_norm -= 0.5;

    o_pixel_coord_norm.x *= iResolution.x/iResolution.y;

    //line
    float n_ires_min =  min(iResolution.x,iResolution.y);
    float n_ires_max =  max(iResolution.x,iResolution.y);
    vec2 o_pix_cor_nor = fragCoord.xy / iResolution.xy; 
    vec2 o_mou_cor_nor = iMouse.xy / iResolution.xy; 
    o_pix_cor_nor-=0.5;
    o_pix_cor_nor*= vec2(
        n_ires_max / n_ires_min,
        n_ires_min / n_ires_min
    );
    o_pix_cor_nor*=2.0; // zoom out


    float n_poly_norm_lowest = 0.;
    float n_polys = min(iResolution.x, iResolution.y)/20.0+430.*o_mou_cor_nor.x; 
    float n_dist_lowest = 1.0;
    float n_radius_norm = 0.0;
    for(float n_poly_norm = 0.0; n_poly_norm<1.0;n_poly_norm+=1.0/n_polys){
        float n_sides = 5.0;
        n_radius_norm += 1.0/n_polys;
        float n_dist_polygon = f_n_dist_polygon_slower(
            n_sides, 
            n_radius_norm,
            sin(n_poly_norm*2.0+iTime*2.0),
            o_pix_cor_nor
        );

        if(n_dist_polygon < n_dist_lowest){
            n_dist_lowest = n_dist_polygon;
            n_poly_norm_lowest = n_poly_norm;
        }
    }


    fragColor = vec4(pow(n_dist_lowest,sin(n_poly_norm_lowest*5.+iTime)*0.5+0.5));
    fragColor = sqrt(fragColor);
}

