vec3 vectorField3D(vec3 p)
{
    float s = 1./512.;
    vec3  n = normalize(vec3(sdTorus(p+s*vec3(1,0,0)) - sdTorus(p-s*vec3(1,0,0)),
                             sdTorus(p+s*vec3(0,1,0)) - sdTorus(p-s*vec3(0,1,0)),
