    vec2 u = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;
    vec3 camPos = step(iMouse.z,.5)*vec3(cos(sin(iTime*.11)*3.),cos(iTime*.7)*.3,sin(sin(iTime*.15)*3.))*3.;
         camPos+= step(.5,iMouse.z)*vec3(cos(m.x),m.y,sin(m.x))*3.;
    vec3 camDir = -normalize(camPos);

    mat3 mtx;
    mtx[0] = normalize(vec3(camDir.z,0.,-camDir.x));
    mtx[1] = cross(camDir,mtx[0]);
    mtx[2] = camDir;
    vec3 ray = mtx*normalize(vec3(u,2.));//direction of ray from camera
    vec3 ray2= 1./ray;
    vec3 ray3= step(vec3(0),ray)*2.-1.;

    float x2 = iResolution.x;
    float x3 = iResolution.x*iResolution.y+.001;
          x3 = pow(x3,1./3.);
          x3 = floor(x3);
    float x3d= 2./x3;
    float x4 = x3*2.;//max voxels the ray will transverse inside simulationCube

        vec3  inc  = step(camPos,vec3( 1))*  //inside cube
                     step(vec3(-1),camPos);
        vec3  tMin = (vec3(-1)-camPos)*ray2;
        vec3  tMax = (vec3( 1)-camPos)*ray2;    ray2 = abs(ray2);
        vec3  t1 = min(tMin, tMax);
        vec3  t2 = max(tMin, tMax);
        float tN = max(max(t1.x, t1.y), t1.z);//length of ray between camera and simulationCube
        float tF = min(min(t2.x, t2.y), t2.z);
              tF = float(tF>tN);

    vec3 p = camPos+ray*tN*(1.-inc.x*inc.y*inc.z)*1.001;//collision position of ray on simulationCube 
         p = (p*.5+.5)*x3;      //transform to voxels coordinates, range 0 to x3
    fragColor = vec4(0);
    for(float i=0.; i<x4; ++i)
    {
        vec3  g = (1.-fract(p*ray3))*ray2;
        float l = min(min(g.x,g.y),g.z);
        fragColor += texelFetch(iChannel0, ivec2(c32(floor(p),x2,x3)), 0)*l;
        p+= ray*l;

        vec3 o = abs(p*x3d-1.);
        if(o.x>=1.||o.y>=1.||o.z>=1.){break;}
    }
    fragColor = fragColor*vec4(.02,.02,.02,1)+fragColor.wwww*.3;
    //fragColor = fragColor.wwww*.7;
    fragColor*= tF;
}

