vec3 vectorField(vec3 p)
{
    return +p.yxz*vec3(-1,1,0)*exp(-dot(p,p)*9.)*.0;
}
void mainImage(out vec4 fragColor, in vec2 u)
{
    float x2 = iResolution.x;
    float x3 = iResolution.x*iResolution.y+.001;
          x3 = pow(x3,1./3.);
          x3 = floor(x3);
    vec3  u3 = c23(floor(u), x2, x3);

    vec3 m = +vectorField(floor(u3)*2./x3-1.)
             +texelFetch(iChannel0, ivec2(u), 0).xyz*1. //mass momentum
             +texelFetch(iChannel1, ivec2(u), 0).xyz;   //mass force on mass
    float s = 0.;
    float z = 2.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
    for(float k=-z; k<=z; ++k){
        vec3 ijk = vec3(i,j,k);
        if(dot(ijk,ijk)>z*z+.1){continue;}
        vec3 c = m+ijk;
        s += exp(-dot(c,c));
    }}}
    if(s!=0.){s = 1./s;}
    fragColor = vec4(m,s);
}

