          x3 = pow(x3,1./3.);
          x3 = floor(x3);
    vec3  u3 = c23(floor(u), x2, x3);

    vec3  v = vec3(0);
    float s = 0.;
    float z = 2.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
    for(float k=-z; k<=z; ++k){
        vec3 ijk = vec3(i,j,k);
        if(dot(ijk,ijk)>z*z+.1){continue;}
        vec3  um = mod(u3+ijk,vec3(x3));
              um = floor(um);
