vec3 c23(vec2 u, float x2, float x3)//transform 2D to 3D coordinates
{
    float i = dot(u,vec2(1,x2))+.5;
    return vec3(mod(i,x3), mod(i/x3,x3), i/(x3*x3));
}
vec2 c32(vec3 u, float x2, float x3)//transform 3D to 2D coordinates
{
    float i = dot(u,vec3(1, x3, x3*x3))+.5;
    return vec2(mod(i,x2), i/x2);
}

