float f_n_dist_line(vec2 p, vec2 a,vec2 b) { // --- distance to segment with caps
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    return length(p - b * h);                      // dist to segment
    // We might directly return smoothstep( 3./R.y, 0., dist),
    //     but its more efficient to factor all lines.
    // We can even return dot(,) and take sqrt at the end of polyline:
    // p -= b*h; return dot(p,p);
}

float f_n_dist_polygon_slower(
    float n_sides,
    float n_radius, 
    vec2 o_pix_cor_nor
){

    float n_tau = 6.283185;
    float n_dist_line_lowest = 1.0; 
    for(
        float n_side_norm = 0.0;
        n_side_norm < 1.0;
        n_side_norm +=1.0/n_sides
       ){
           float n_side_norm_next = n_side_norm+(1.0/n_sides);

           vec2 p1 = vec2(
               sin(n_tau*n_side_norm)*n_radius,
               cos(n_tau*n_side_norm)*n_radius
           );
           vec2 p2 = vec2(
               sin(n_tau*n_side_norm_next)*n_radius,
               cos(n_tau*n_side_norm_next)*n_radius
           );


           float n_dist = f_n_dist_line(o_pix_cor_nor, p1,p2);

           if(n_dist < n_dist_line_lowest){
               n_dist_line_lowest = n_dist;
           }
       }


    return n_dist_line_lowest;
}

float f_n_dist_polygon(
    float n_sides,
    float n_radius, 
    vec2 o_pix_cor_nor
){

    float n_tau = 6.283185;
    float n_dist_line_lowest = 1.0;

    float n_center_xy = 0.0;
    float delta_x = o_pix_cor_nor.x - n_center_xy;
    float delta_y = o_pix_cor_nor.y - n_center_xy;

    float n_rad_current = atan(0.0, 0.0) - atan(o_pix_cor_nor.x, o_pix_cor_nor.y);
    n_rad_current = atan(delta_x,delta_y);
    float n_nth_line = floor(n_rad_current/(n_tau / n_sides));
    float n_radians = (n_tau / n_sides) * (n_nth_line);
    float n_radians_next = (n_tau / n_sides) * ((n_nth_line)+1.0);

    //float n_radians_next = n_radians+ (n_tau / n_sides);

   vec2 p1 = vec2(
       sin((n_nth_line)*(n_tau / n_sides))*n_radius,
       cos((n_nth_line)*(n_tau / n_sides))*n_radius
   );
   vec2 p2 = vec2(
       sin(n_radians_next)*n_radius,
       cos(n_radians_next)*n_radius
   );

   float n_dist = f_n_dist_line(o_pix_cor_nor,p2,p1);

    return n_dist;
}

