#define thc(a,b) tanh(a*cos(b))/tanh(a)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float a = atan(uv.y, uv.x);
    vec2 p = 0.5 * cos(a - 0.49 * iTime) * vec2(cos(0.5 * iTime), sin(0.3 * iTime));
    vec2 q = 0.5 * (cos(2. * a - 0.51 * iTime)) * vec2(cos(iTime), sin(iTime));

    float d1 = length(uv-q) / length(uv - p) + 1.;
    float d2 = length(uv - q) / length(uv) + 1.;

    vec2 uv2 = vec2(d1,d2) / (d1 + d2);
    uv2 = log(uv2 + 0.495);
    a = atan(uv2.y, uv2.x) + 4. * log(length(uv2)) + iTime;
    uv2 *= vec2(sin(a), cos(a));
    vec3 col = vec3(uv2.x, uv2.y, abs(uv2.x-uv2.y));
    col = exp(-24. * 0.5 * col);
    col *= 0.5 +0.5 * thc(4., a -10. * log(length(uv)));
    col += exp(-3.5 * length(uv))-exp(-8. * length(uv2));
    col = 0.5 * col + 0.5 * clamp(col,0.,1.);
    //col = clamp(col,0.,1.);
    col += vec3(0.9,0.6,0.45) * thc(4., exp(-10. * length(uv-uv2))) * abs(d1/d2-d2/d1);
    col -= 0.7 / cosh(4. * length(uv)) * vec3(0.4,0.58,1);

    fragColor = vec4(col,1.0);
}

float line(vec2 p, vec2 a,vec2 b) { // --- distance to segment with caps
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    return length(p - b * h);                      // dist to segment
    // We might directly return smoothstep( 3./R.y, 0., dist),
    //     but its more efficient to factor all lines.
    // We can even return dot(,) and take sqrt at the end of polyline:
    // p -= b*h; return dot(p,p);
}

