/*

The idea of rational bezier curves is basically that you
define the points in homogenic coordinates, different
representatives of the same affine points correspond to different
weighted points. The curve is then also computed in homogenic coordinates
and then you divide by the third coordinate to get an affine point.

So instead of a parametric polynomial function,
you get a parametric rational function.

Every three points have a weight associated a priori.

But scaling all three weights by the same factor doesn't change the curve.
So you can set one of the weights to 1.

I decided to set two weights to 1 and only change the weight at the
control point, but i'm loosing a degree of freedom with that.

I compute the approximated signed distance to the curve by using the implicitization
and gradient distance estimation.

For more information about all sorts of stuff
regarding bezier curves i always recommend Sederbergs script:
http://hdl.lib.byu.edu/1877/2822

There is probably room for improvement at different parts of this shader.
*/

//#define SHOW_DISTANCE_FIELD

const bool fill_outer=false;

const float dot_size=.003;

const vec3 bg_col=vec3(1);
const vec3 dot_col=vec3(0,1,0);
const vec3 fill_col=vec3(0);

const float pi=3.1415925;

const int num_iterations=3;
const int num_start_params=16;

float rational_quadratic_bezier_gradient_dis_approx(vec2 uv,vec3 p0,vec3 p1,vec3 p2){
	vec3 a2=p0-2.*p1+p2;
	vec3 a1=-2.*p0+2.*p1;
	vec3 a0=p0;

	vec2 b2=a2.xy-uv*a2.z;
	vec2 b1=a1.xy-uv*a1.z;
	vec2 b0=a0.xy-uv*a0.z;

    float dm21=determinant(mat2(b2,b1));
    float dm20=determinant(mat2(b2,b0));
    float dm10=determinant(mat2(b1,b0));

	mat2 bezout_matrix=mat2(dm21,dm20,dm20,dm10);
    float val=determinant(bezout_matrix);

	float grad_x=2.*dm20*(a2.z*b0.y - a0.z*b2.y)
	             - dm10*(a2.z*b1.y - a1.z*b2.y)
				 - dm21*(a1.z*b0.y - a0.z*b1.y);

	float grad_y=dm10*(a2.z*b1.x - a1.z*b2.x)
				 + dm21*(a1.z*b0.x - a0.z*b1.x)
				 - 2.*dm20*(a2.z*b0.x - a0.z*b2.x);

	return val/length(vec2(grad_x,grad_y));
}

// quartic equation solver by oneshade (see https://www.shadertoy.com/view/fsB3Wt)
float cbrt(in float x) { return sign(x) * pow(abs(x), 1.0 / 3.0); }
int solveQuartic(in float a, in float b, in float c, in float d, in float e, inout vec4 roots) {
    b /= a; c /= a; d /= a; e /= a; // Divide by leading coefficient to make it 1

    // Depress the quartic to x^4 + px^2 + qx + r by substituting x-b/4a
    // This can be found by substituting x+u and the solving for the value
    // of u that makes the t^3 term go away
    float bb = b * b;
    float p = (8.0 * c - 3.0 * bb) / 8.0;
    float q = (8.0 * d - 4.0 * c * b + bb * b) / 8.0;
    float r = (256.0 * e - 64.0 * d * b + 16.0 * c * bb - 3.0 * bb * bb) / 256.0;
    int n = 0; // Root counter

    // Solve for a root to (t^2)^3 + 2p(t^2)^2 + (p^2 - 4r)(t^2) - q^2 which resolves the
    // system of equations relating the product of two quadratics to the depressed quartic
    float ra =  2.0 * p;
    float rb =  p * p - 4.0 * r;
    float rc = -q * q;

    // Depress using the method above
    float ru = ra / 3.0;
    float rp = rb - ra * ru;
    float rq = rc - (rb - 2.0 * ra * ra / 9.0) * ru;

    float lambda;
    float rh = 0.25 * rq * rq + rp * rp * rp / 27.0;
    if (rh > 0.0) { // Use Cardano's formula in the case of one real root
        rh = sqrt(rh);
        float ro = -0.5 * rq;
        lambda = cbrt(ro - rh) + cbrt(ro + rh) - ru;
    }

    else { // Use complex arithmetic in the case of three real roots
        float rm = sqrt(-rp / 3.0);
        lambda = -2.0 * rm * sin(asin(1.5 * rq / (rp * rm)) / 3.0) - ru;
    }

    // Newton iteration to fix numerical problems (using Horners method)
    // Suggested by @NinjaKoala
    for(int i=0; i < 2; i++) {
        float a_2 = ra + lambda;
        float a_1 = rb + lambda * a_2;
        float b_2 = a_2 + lambda;

        float f = rc + lambda * a_1; // Evaluation of ^3 + ra * ^2 + rb *  + rc
        float f1 = a_1 + lambda * b_2; // Derivative

        lambda -= f / f1; // Newton iteration step
    }

    // Solve two quadratics factored from the quartic using the cubic root
    if (lambda < 0.0) return n;
    float t = sqrt(lambda); // Because we solved for t^2 but want t
    float alpha = 2.0 * q / t, beta = lambda + ra;

    float u = 0.25 * b;
    t *= 0.5;

    float z = -alpha - beta;
    if (z > 0.0) {
        z = sqrt(z) * 0.5;
        float h = +t - u;
        roots.xy = vec2(h + z, h - z);
        n += 2;
    }

    float w = +alpha - beta;
    if (w > 0.0) {
        w = sqrt(w) * 0.5;
        float h = -t - u;
        roots.zw = vec2(h + w, h - w);
        if (n == 0) roots.xy = roots.zw;
        n += 2;
    }

    return n;
}

vec2 parametric_rational_quadratic_bezier(float t, vec3 p0, vec3 p1, vec3 p2){
    vec3 p = mix(mix(p0,p1,t),mix(p1,p2,t),t);
    return p.xy/p.z;
}

/* Exact variant, has precision issues */
float rational_quadratic_bezier_dis_exact(vec2 uv,vec3 p0,vec3 p1,vec3 p2){
	vec3 a2=p0-2.*p1+p2;
	vec3 a1=-2.*p0+2.*p1;
	vec3 a0=p0;

	float q4 = (a2.x*a2.x + a2.y*a2.y)*a1.z
             - (a1.x*a2.x + a1.y*a2.y)*a2.z
             - (a2.x*a1.z*a2.z - a1.x*a2.z*a2.z)*uv.x
             - (a2.y*a1.z*a2.z - a1.y*a2.z*a2.z)*uv.y;

    float q3 = 2.*(a2.x*a2.x + a2.y*a2.y)*a0.z
             + (a1.x*a2.x + a1.y*a2.y)*a1.z
             - (a1.x*a1.x + 2.*a0.x*a2.x
             + a1.y*a1.y + 2.*a0.y*a2.y)*a2.z
             - (a2.x*a1.z*a1.z - 2.*a0.x*a2.z*a2.z
             + (2.*a2.x*a0.z - a1.x*a1.z)*a2.z)*uv.x
             - (a2.y*a1.z*a1.z - 2.*a0.y*a2.z*a2.z
             + (2.*a2.y*a0.z - a1.y*a1.z)*a2.z)*uv.y;

    float q2 = 3.*((a1.x*a2.x + a1.y*a2.y)*a0.z
             - (a0.x*a1.x + a0.y*a1.y)*a2.z
             - (a2.x*a0.z*a1.z - a0.x*a1.z*a2.z)*uv.x
             - (a2.y*a0.z*a1.z - a0.y*a1.z*a2.z)*uv.y);

    float q1 = (a1.x*a1.x + 2.*a0.x*a2.x + a1.y*a1.y + 2.*a0.y*a2.y)*a0.z
             - (a0.x*a1.x + a0.y*a1.y)*a1.z
             - 2.*(a0.x*a0.x + a0.y*a0.y)*a2.z
             - (2.*a2.x*a0.z*a0.z + a1.x*a0.z*a1.z
             - a0.x*a1.z*a1.z - 2.*a0.x*a0.z*a2.z)*uv.x
             - (2.*a2.y*a0.z*a0.z + a1.y*a0.z*a1.z
             - a0.y*a1.z*a1.z - 2.*a0.y*a0.z*a2.z)*uv.y;

    float q0 = (a0.x*a1.x + a0.y*a1.y)*a0.z
             - (a0.x*a0.x + a0.y*a0.y)*a1.z
             - (a1.x*a0.z*a0.z - a0.x*a0.z*a1.z)*uv.x
             - (a1.y*a0.z*a0.z - a0.y*a0.z*a1.z)*uv.y;

    vec4 roots;
    int n_roots = solveQuartic(q4,q3,q2,q1,q0,roots);

    float dis = 1e38;

    for(int i=0;i<n_roots;i++){
        dis = min(dis, distance(uv,parametric_rational_quadratic_bezier(roots[i],p0,p1,p2)));
    }

    return dis;
}

float rational_quadratic_bezier_val_grad(vec2 uv,vec3 p0,vec3 p1,vec3 p2, out vec2 grad){
    vec3 a2=p0-2.*p1+p2;
	vec3 a1=-2.*p0+2.*p1;
	vec3 a0=p0;

    vec2 b2=a2.xy-uv*a2.z;
    vec2 b1=a1.xy-uv*a1.z;
    vec2 b0=a0.xy-uv*a0.z;

    float dm21=determinant(mat2(b2,b1));
    float dm20=determinant(mat2(b2,b0));
    float dm10=determinant(mat2(b1,b0));

    mat2 bezout_matrix=mat2(dm21,dm20,dm20,dm10);
    float val=determinant(bezout_matrix);

    float grad_x=2.*dm20*(a2.z*b0.y - a0.z*b2.y)
                 - dm10*(a2.z*b1.y - a1.z*b2.y)
                 - dm21*(a1.z*b0.y - a0.z*b1.y);

    float grad_y=dm10*(a2.z*b1.x - a1.z*b2.x)
                 + dm21*(a1.z*b0.x - a0.z*b1.x)
                 - 2.*dm20*(a2.z*b0.x - a0.z*b2.x);

    grad = vec2(grad_x,grad_y);

    return val;
}

float rational_quadratic_bezier_dis_approx2d(vec2 uv,vec3 p0,vec3 p1,vec3 p2){
    const int num_start_points = 2;
    const int num_iterations = 10;

    float dis = 1e38;

    for(int i=0;i<num_start_points;i++){
        float t = float(i) / float(num_start_points-1);
        vec2 p = parametric_rational_quadratic_bezier(t,p0,p1,p2);

        for(int j=0;j<num_iterations;j++){
            vec2 grad;
            float val = rational_quadratic_bezier_val_grad(p,p0,p1,p2,grad);
            vec2 tang = normalize(vec2(grad.y,-grad.x));
            p += dot(tang,uv-p) * tang;

            val = rational_quadratic_bezier_val_grad(p,p0,p1,p2,grad);
            p -= val * grad / dot(grad,grad);
        }
        dis = min(dis,distance(uv,p)); 
    }

    return dis;
}

// Netwon method
float rational_quadratic_bezier_dis_newton(vec2 uv, vec3 p0, vec3 p1, vec3 p2){
    const float eps = .001;
    const int num_start_vals = 16;
    const int num_iterations = 5;

    float dis = 1e38;

    for(int i=0;i<num_start_vals;i++){
        float t = float(i) / float(num_start_vals-1);

        for(int j=0;j<num_iterations;j++){
            vec3 fp1 = mix(mix(p0,p1,t-eps),mix(p1,p2,t-eps),t-eps);
            float f1 = dot(uv-fp1.xy/fp1.z,uv-fp1.xy/fp1.z);

            vec3 fp2 = mix(mix(p0,p1,t),mix(p1,p2,t),t);
            float f2 = dot(uv-fp2.xy/fp2.z,uv-fp2.xy/fp2.z);

            vec3 fp3 = mix(mix(p0,p1,t+eps),mix(p1,p2,t+eps),t+eps);
            float f3 = dot(uv-fp3.xy/fp3.z,uv-fp3.xy/fp3.z);

            t -= eps * ((-.5 * f1 + .5 * f3) / (f1 - 2.*f2 + f3));
        }
        t = clamp(t,0.,1.);
        vec3 p = mix(mix(p0,p1,t),mix(p1,p2,t),t);
        float f = dot(uv-p.xy/p.z,uv-p.xy/p.z);

        dis = min(dis,f);
    }
    return sqrt(dis);
}

//by iq (see https://www.shadertoy.com/view/XsXSz4)
float sdTriangle(vec2 p, vec2 p0, vec2 p1, vec2 p2 ){
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float rational_quadratic_bezier_normal_iteration(float t, vec2 uv, vec3 a0, vec3 a1, vec3 a2){
	vec3 eval=(a2*t+a1)*t+a0;

	vec2 diff_num=((a2.xy*a1.z - a1.xy*a2.z)*t + 2.*(a2.xy*a0.z - a0.xy*a2.z))*t + a1.xy*a0.z - a0.xy*a1.z;

	vec2 uv_to_p=eval.xy / eval.z - uv;
	vec2 tang=diff_num / (eval.z * eval.z);

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float rational_quadratic_bezier_dis_approx(vec2 uv, vec3 p0, vec3 p1, vec3 p2){
	vec3 a2 = p0 - 2. * p1 + p2;
	vec3 a1 = -2. * p0 + 2. * p1;
	vec3 a0 = p0;

	float d0 = 1e38;

	float t0=.0;
	float t;

	for(int i=0;i<num_start_params;i++){
		t=t0;
		for(int j=0;j<num_iterations;j++){
			t=rational_quadratic_bezier_normal_iteration(t,uv,a0,a1,a2);
		}
		t=clamp(t,0.,1.);
		vec3 cur_p=(a2*t+a1)*t+a0;
		cur_p/=cur_p.z;
		vec2 uv_to_p = cur_p.xy - uv;
		d0=min(d0,dot(uv_to_p,uv_to_p));

		t0+=1./float(num_start_params-1);
	}

	return sqrt(d0);
}

float rational_quadratic_bezier_sign(vec2 uv,vec3 p0,vec3 p1,vec3 p2){
	vec3 a2=p0-2.*p1+p2;
	vec3 a1=-2.*p0+2.*p1;
	vec3 a0=p0;

	vec2 b2=a2.xy-uv*a2.z;
	vec2 b1=a1.xy-uv*a1.z;
	vec2 b0=a0.xy-uv*a0.z;

	mat2 bezout_matrix=mat2(determinant(mat2(b2,b1)),determinant(mat2(b2,b0)),determinant(mat2(b2,b0)),determinant(mat2(b1,b0)));

	return sign(determinant(bezout_matrix));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	vec2 mouse2=abs(iMouse.zw)/iResolution.xy;
	mouse2-=.5;
    mouse2.y*=iResolution.y/iResolution.x;

	float weight = exp(5.*(mouse2.y-mouse.y));

	vec3 p0=vec3(-.3,-.2,1.);
	vec3 p1=vec3(mouse2,1.)*weight;
	vec3 p2=vec3(.3,-.2,1.);

	if(max(iMouse.x, iMouse.y) < 20.){
		weight=1.+.3*sin(2.*iTime);
        p1=vec3(.3*sin(iTime),.1+.1*cos(iTime),1.)*weight;
    }

	float tri_dis=sdTriangle(uv,p0.xy/p0.z,p1.xy/p1.z,p2.xy/p2.z);

    float curve_dis=(fill_outer?1.0:-1.0) * rational_quadratic_bezier_gradient_dis_approx(uv,p0,p1,p2);

    // The distance approximation using the parametric form doesn't work well here
    /*
    float sgn=rational_quadratic_bezier_sign(uv,p0,p1,p2);
    float curve_dis=(fill_outer?1.0:-1.0) * sgn * rational_quadratic_bezier_dis_approx(uv,p0,p1,p2);
    */

    // Exact variant (with numerical precision issues)
    /*
    float sgn=rational_quadratic_bezier_sign(uv,p0,p1,p2);
    float curve_dis=(fill_outer?1.0:-1.0) * sgn * rational_quadratic_bezier_dis_exact(uv,p0,p1,p2);
    */

    // Newton method variant
    /*
    float sgn=rational_quadratic_bezier_sign(uv,p0,p1,p2);
    float curve_dis=(fill_outer?1.0:-1.0) * sgn * rational_quadratic_bezier_dis_newton(uv,p0,p1,p2);
    */

    #ifdef SHOW_DISTANCE_FIELD
    float dis = curve_dis;
    #else
	float dis=max(tri_dis,curve_dis);
    #endif

	vec3 color = bg_col;
	color=mix(fill_col,color,smoothstep(-border/2.,border/2.,dis));

    #ifdef SHOW_DISTANCE_FIELD
    dis*=3.;
    vec3 col = vec3(1.0) - sign(dis)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(dis));
	col *= 0.8 + 0.2*cos(120.0*dis);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(dis)) );
    color = col;
    #endif

	dis=1e38;

	dis=min(dis,distance(p0.xy/p0.z,uv)-dot_size);
	dis=min(dis,distance(p1.xy/p1.z,uv)-dot_size*weight);
	dis=min(dis,distance(p2.xy/p2.z,uv)-dot_size);

    vec2 p3=parametric_rational_quadratic_bezier(fract(.1*iTime),p0,p1,p2);
    dis=min(dis,distance(p3,uv)-dot_size);

	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));

	fragColor=vec4(color,1);
}

// Reference: https://www.datagenetics.com/blog/november12011/
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
