            for (int idx=0; idx < 19; idx++) {
                if (slides[idx].x == i) {
                    isSlide = true;
                    dest = slides[idx].y;
                    break;
                }
            }

            if (isSlide) {
                if (j == dest) {
                    fragColor = vec4(1.0); // Instantly jump to a different square
                }
            }

            else {
                if (j >= i + 1 && j < i + 7) {
                    fragColor = vec4(1.0); // Advance forward amount rolled on the die
                }

                if (i > 93 && j == i) {
                    fragColor = vec4(i - 93); // Must get exact amount to finish
                }

                fragColor /= 6.0;
            }
