//CBS
//Parallax scrolling fractal galaxy.
//Inspired by JoshP's Simplicity shader: https://www.shadertoy.com/view/lslGWr

// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/
float field(in vec3 p,float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 26; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

// Less iterations for second layer
float field2(in vec3 p, float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 18; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

vec3 nrand3( vec2 co )
{
	vec3 a = fract( cos( co.x*8.3e-3 + co.y )*vec3(1.3e5, 4.7e5, 2.9e5) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e5, 0.1e5) );
	vec3 c = mix(a, b, 0.5);
	return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y);
	vec3 p = vec3(uvs / 4., 0) + vec3(1., -1.3, 0.);
	p += .2 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));

	float freqs[4];
	//Sound
	freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;

	float t = field(p,freqs[2]);
	float v = (1. - exp((abs(uv.x) - 1.) * 6.)) * (1. - exp((abs(uv.y) - 1.) * 6.));

    //Second Layer
	vec3 p2 = vec3(uvs / (4.+sin(iTime*0.11)*0.2+0.2+sin(iTime*0.15)*0.3+0.4), 1.5) + vec3(2., -1.3, -1.);
	p2 += 0.25 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));
	float t2 = field2(p2,freqs[3]);
	vec4 c2 = mix(.4, 1., v) * vec4(1.3 * t2 * t2 * t2 ,1.8  * t2 * t2 , t2* freqs[0], t2);


	//Let's add some stars
	//Thanks to http://glsl.heroku.com/e#6904.0
	vec2 seed = p.xy * 2.0;	
	seed = floor(seed * iResolution.x);
	vec3 rnd = nrand3( seed );
	vec4 starcolor = vec4(pow(rnd.y,40.0));

	//Second Layer
	vec2 seed2 = p2.xy * 2.0;
	seed2 = floor(seed2 * iResolution.x);
	vec3 rnd2 = nrand3( seed2 );
	starcolor += vec4(pow(rnd2.y,40.0));

	fragColor = mix(freqs[3]-.3, 1., v) * vec4(1.5*freqs[2] * t * t* t , 1.2*freqs[1] * t * t, freqs[3]*t, 1.0)+c2+starcolor;
}

// macro for smoothstep
#define f_smstp(a,b,t) smoothstep(a,b,t)
float f_n_remap1(
    float a,
    float b,
    float t
){
    return clamp((t-a)/(b-a), 0.0, 1.0);
}
float f_n_remap2(
    float a,
    float b,
    float c,
    float d,
    float t
){
    return (f_n_remap1(a,b,t)) * (d-c) + c;
}
vec2 f_o_within(
    vec2 o_pixel_coord_norm, 
    vec4 o_rect
){
    return (o_pixel_coord_norm-o_rect.xy)/(o_rect.zw-o_rect.xy);
}
vec4 f_o_col_eye(
    vec2 o_pixel_coord_norm
){
    // re normalize 
    o_pixel_coord_norm -= 0.5;
    float n_dist = length(o_pixel_coord_norm);

    vec4 o_col_iris = vec4(0.3, 0.5, 1., 1.0);
    vec4 o_col = mix(vec4(1.0), o_col_iris, f_smstp(0.1, 0.5, n_dist)*0.5);

    o_col.rgb *= 1. - f_smstp(0.45, 0.5 , n_dist)*0.5*clamp(-o_pixel_coord_norm.y-o_pixel_coord_norm.x, 0.0, 1.0);

    o_col.rgb = mix(o_col.rgb, vec3(0.0), f_smstp(0.3, 0.25, n_dist));
    o_col_iris.rgb *= 1. + f_smstp(0.3, 0.05, n_dist);


    o_col.rgb = mix(o_col.rgb, o_col_iris.rgb, f_smstp(0.28, 0.25, n_dist));

    o_col.rgb = mix(o_col.rgb, vec3(0.0), f_smstp(0.16, 0.14, n_dist));


    float n_highlight = f_smstp(
        0.1,
        0.09,
        length(
            o_pixel_coord_norm
            - vec2(-.15, .15)
        )
    );
    n_highlight += f_smstp(
        0.07,
        0.05,
        length(
            o_pixel_coord_norm
            + vec2(-.08, .08)
        )
    );
    o_col.rgb = mix(o_col.rgb, vec3(1.0), n_highlight);
    o_col.a = f_smstp(0.5, 0.48, n_dist);


    return o_col;
}

vec4 f_o_col_mouth(
    vec2 o_pixel_coord_norm
){
    o_pixel_coord_norm -= 0.5;
    o_pixel_coord_norm.y *= 1.5;

    o_pixel_coord_norm.y -= o_pixel_coord_norm.x * o_pixel_coord_norm.x*2.0;
    vec4 o_col = vec4(0.5, 0.18, 0.05 , 1.0);
    float n_dist = length(o_pixel_coord_norm);
    o_col.a = f_smstp(0.5, 0.48, n_dist);

    vec3 o_col_tooth = vec3(1.0)*f_smstp(0.6, 0.35, n_dist);
    float n_dist_teeth  = length(o_pixel_coord_norm-vec2(0., 0.55)); 
    o_col.rgb = mix(o_col.rgb, o_col_tooth, f_smstp(0.4, 0.37, n_dist_teeth));

    float n_dist_tounge = length(o_pixel_coord_norm+vec2(0., 0.5));
    o_col.rgb = mix(o_col.rgb, vec3(1.0, 0.5, 0.5), f_smstp(0.5, 0.2, n_dist_tounge));

    return o_col;
}

vec4 f_o_col_head(
    vec2 o_pixel_coord_norm
){
    vec4 o_col = vec4(0.9, 0.66, 0.3, 1.0);
    float n_dist = length(
        vec2(0.0,0.0) - o_pixel_coord_norm
    );
    o_col.a = f_smstp(0.5, 0.49, n_dist);
    float n_edge_shade = f_n_remap1(.35, .5, n_dist);
    n_edge_shade = n_edge_shade*n_edge_shade;
    o_col.rgb *= 1.-n_edge_shade * 0.3;
    o_col.rgb = mix(o_col.rgb, vec3(0.7, 0.3, 0.1), f_smstp(0.47, 0.48, n_dist));

    float n_highlight = f_smstp(0.41, 0.405, n_dist)*0.5;
    n_highlight *= f_n_remap2(0.41, 0.0, 0.76, 0., o_pixel_coord_norm.y);
    o_col.rgb = mix(o_col.rgb, vec3(1.0), n_highlight);

    n_dist = length(o_pixel_coord_norm - vec2(0.25, -0.2));
    float n_cheeck = f_smstp(0.2, 0.01, n_dist)*0.4;
    n_cheeck *= f_smstp(.17, 0.15, n_dist);
    o_col.rgb = mix(o_col.rgb, vec3(1.0, 0.1, 0.1), n_cheeck);
    return o_col;
}

vec4 f_o_col_smiley(
    vec2 o_pixel_coord_norm
){
    vec4 o_col = vec4(0.0);
    //mirror x coordinate 
    o_pixel_coord_norm.x = abs(o_pixel_coord_norm.x);

    vec4 o_col_head = f_o_col_head(o_pixel_coord_norm);

    vec4 o_col_eye = f_o_col_eye(
        f_o_within(
            o_pixel_coord_norm, 
            vec4(0.03, -.1, 0.37, 0.25)
        )
    );

    vec4 o_col_mouth = f_o_col_mouth(
        f_o_within(
            o_pixel_coord_norm, 
            vec4(-.3, -.4, 0.3, -0.1)
        )
    );

    o_col = mix(o_col, o_col_head, o_col_head.a);
    o_col = mix(o_col, o_col_eye, o_col_eye.a);
    o_col = mix(o_col, o_col_mouth, o_col_mouth.a);



    return o_col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pixel_coord_norm = fragCoord.xy / iResolution.xy;

    o_pixel_coord_norm -= 0.5;

    o_pixel_coord_norm.x *= iResolution.x/iResolution.y;

    fragColor = f_o_col_smiley(o_pixel_coord_norm);
}

