//@Shane
vec4 hexGrid(vec2 uv, float scale){
    uv*=scale;
    const vec2 s = vec2(SQ3, 1.);
    vec4 hC = floor(vec4(uv, uv - vec2(1, .5))/s.xyxy) + .5;
    vec4 h4 = vec4(uv - hC.xy*s, uv - (hC.zw + .5)*s);
    return dot(h4.xy, h4.xy) < dot(h4.zw, h4.zw) ? vec4(h4.xy, hC.xy) : vec4(h4.zw, hC.zw + .5);
}

float box( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

const float scale = 11.;
void mainImage( out vec4 O, in vec2 F )
{
    vec3 C = vec3(0);

    vec2 uv = F.xy/max(R.x,R.y);
    uv-=vec2(T*.01,T*.05);
    vec2 vuv=uv;

    float px = scale/R.x;
    vec4 hex = hexGrid(uv,scale);

    vec2 id = hex.zw;
    vec2 p  = hex.xy;

    float check = mod(id.y + id.x,2.) * 2. - 1.;
    float rnd = hash21(id);
    float dir =  -1.;

    if(rnd>.5) {
        p *= rot(1.0471975512);
        p.y=-p.y;
    } 

    float rdx = .2885;

    // set vectors
    vec2 p0 = p - vec2(-.5/SQ3, .5);
    vec2 p1 = p - vec2(.8660254*2./3., 0);
    vec2 p2 = p - vec2(-.5/SQ3, -.5);

    // find closest point
    vec3 d3 = vec3(length(p0), length(p1), length(p2));
    vec2 pp = vec2(0);

    if(d3.x>d3.y) pp = p1;
    if(d3.y>d3.z) pp = p2;
    if(d3.z>d3.x && d3.y>d3.x) pp = p0;

    float wrp = sin(T+(vuv.x+vuv.y)*10.);
    // draw truchet path
    float circle = length(pp)-rdx;
    circle=abs(circle)-(.14+.080*wrp);//.15;

    float cntr = circle;
    float cntrsdo = circle;
    cntrsdo = smoothstep(.1-px,px,cntrsdo);
    cntr = smoothstep(px,-px,cntr);

    circle=abs(circle)-.0075;
    circle=smoothstep(px,-px,circle);
    float amt = 12.;

    // hex background
    float tileform = max(abs(hex.x)*.8660254 + abs(hex.y)*.5, abs(hex.y)) - .5;
    float edges = abs(abs(tileform)-.025)-.015;
    edges=smoothstep(px,-px, edges);
    float cells=smoothstep(px,-px, tileform);

    //animation
    d3 = abs(d3 - SQ3/6.) - .125;
    vec3 a3=vec3(atan(p0.x, p0.y),atan(p1.x, p1.y),atan(p2.x, p2.y));

    vec2 da = vec2(0);
    if(d3.x>d3.y) da = vec2(d3.y, a3.y);
    if(d3.y>d3.z) da = vec2(d3.z, a3.z);
    if(d3.z>d3.x && d3.y>d3.x) da = vec2(d3.x, a3.x);

    //speed
    float speed = texture(iChannel1,(F/8.)).r - (T*2.);

    float d = length(pp);
    float pathMotion = 200.+da.y/ PI2* (amt) + speed;

    float x =fract(pathMotion) - .5;
    float y = d-rdx;

    vec2 cid = vec2(floor(x),floor(pathMotion)-.5);
    cid=mod(cid,4.);

    //vector and compress coords
    vec2 tu = vec2(x,y)*vec2(3,10.);

    float path = box(tu,vec2(1.5,1.1+.75*wrp));
    path=smoothstep(px,-px,path);

    //mixdowns and stuff
    vec3 topHue = hsv2rgb(vec3(cid.y*PI2,1.,.6)).xxx;
    float cut = min(cells,1.-cntr);


    C = rnd>.7? vec3(.5):vec3(.05);
    C = mix(C, vec3(.9), cntr);
    C = mix(C, vec3(.05),cntrsdo);

    C = mix(C, topHue,path);
    C = mix(C, vec3(.0),min(cut,edges));
    C = mix(C, vec3(.85),circle); 

    //output
    O = vec4(pow(C,vec3(.4545)),1.0);
}

#define PI 3.14159

vec2 hash22(vec2 p){
    vec2 a = vec2(94.86, 43.46);
    vec2 b = vec2(72.67, 13.48);
    p = vec2(dot(p, a), dot(p, b));
    return fract(sin(p*10.29)*48.47);
}

vec3 hash23(vec2 p){
    vec2 a = vec2(92.8, 438.7);
    vec2 b = vec2(73.6, 12.8);
    vec2 c = vec2(34.7, 73.18);

    vec3 r = vec3(dot(p, a), dot(p, b), dot(p, c));
    return fract(sin(r*10.29)*48.47);
}
vec3 analytic_sphere(vec2 p, float r) {
    //return sphere_coord;
    float l = length(p);
    float h = r*cos(l*PI/2./(r*1.5));
    p = normalize(p/r)*asin(length(p/r)) / PI;
    //p *= smoothstep(0., 0.001, sign(p));
    return vec3(p, h);
}

vec3 calcNormal(vec2 p, float r){
    vec2 e = vec2(1.0, -1.0) * 0.001;    
    return normalize(
      e.xyx*analytic_sphere(p + e.xy, r) +
      e.yxx*analytic_sphere(p + e.yx, r) +
      e.xxx*analytic_sphere(p + e.xx, r));
}

vec2 voro(vec2 uv, float ntiles, out vec3 normal, out vec3 col_boule)
{
    vec2 uv_id = floor (uv*ntiles);
    vec2 uv_st = fract(uv*ntiles);

    vec2 m_neighbor, m_diff, m_neighbor_id;
    float m_dist_s, m_dist = 10.;
    vec2 idx = vec2(0.,0.);
    int K = 1;
    vec2 point;
    for (int j = -K; j<=K; j++)
    {
        for (int i = -K; i<=K; i++)
        {
            vec2 neighbor = vec2(float(i), float(j));
            if (uv_id + neighbor == vec2(0.,0.)){
                point = (iMouse.xy-.5*iResolution.xy)/iResolution.y*ntiles;
            }
            else{
                point = hash22(uv_id + neighbor);
                point = float(K)/2.+float(K)/2.*sin(2.*PI*point+iTime/1.);
            }
            vec2 diff = neighbor + point - uv_st;
            float dist = length(diff);
            if (dist < m_dist)
            {
                m_dist = dist;
                m_dist_s = dist;
                m_diff = diff;
                m_neighbor = neighbor+point;
                m_neighbor_id = neighbor;
            }
        }
    }

    // mla suggestion
    vec2 neighbor_id = m_neighbor_id + uv_id;
    vec2 neighbor_coord = m_neighbor + uv_id - neighbor_id;

    m_dist=100.;

    for (int j = -K-1; j<=K+1; j++)
    {
        for (int i = -K-1; i<=K+1; i++)
        {
            vec2 neighbor = vec2(float(i), float(j));
            if (neighbor_id + neighbor == vec2(0.,0.)){
                point = (iMouse.xy-.5*iResolution.xy)/iResolution.y*ntiles;
            }
            else{
                point = hash22(neighbor_id + neighbor);
                point = float(K)/2.+float(K)/2.*sin(2.*PI*point+iTime/1.);
            }
            vec2 new_neighbor = point+neighbor;
            vec2 diff = new_neighbor - neighbor_coord;
            float dist = length(diff);
            if (dist < m_dist && length(neighbor_coord-new_neighbor)>.0001)
                    m_dist = dist;
        }
    }
    float neighbor_radius = m_dist/2.;


    float d = 1.-smoothstep(-.02, -0., length(m_neighbor-uv_st)-neighbor_radius);

    float r = neighbor_radius;
    vec2 p = m_neighbor-uv_st;
    float l = length(p);
    vec3 sphere = analytic_sphere(p, r);
    float h = r*cos(l*PI/2./r);
    col_boule = hash23(neighbor_id);
    normal = calcNormal(p, r);

    return vec2(d, h);
}

float smooth_damier(vec2 uv){
    float a = (.32035*atan(sin(PI*uv.x)/0.01)+.5);
    float b = (.32035*atan(sin(PI*uv.y)/0.01)+.5);
    return a*(1.-b)+b*(1.-a);
}

vec3 phong(vec3 lightDir, vec3 normal, vec3 rd, vec3 col) {
  // ambient
  vec3 ambient = col*(.5+.5*normal.z);

  // diffuse
  float dotLN = clamp(dot(lightDir, normal), 0., 1.);
  vec3 diffuse = col * dotLN;

  // specular
  float dotRV = clamp(dot(reflect(lightDir, normal*.8), -rd), 0., 1.);
  vec3 specular = 2.*col* pow(dotRV, 12.);

  return ambient*.4 + diffuse + specular;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    // Time varying pixel color

    vec3 normal;
    vec3 col_boule;
    vec2 v = voro(uv, 4., normal, col_boule);

    vec3 rd = vec3(uv, 2.);
    vec3 ld = vec3(-.4,-.4, 2.);//lightdir


    vec3 rr = refract(rd, normal, .1);
    vec3 ro = vec3(uv, v.y);
    float depth = -ro.z/rr.z;
    vec2 st = (v.x > 0.06) ? (ro+rr*depth).xy : uv;


    vec3 col = v.y*1.*max(vec3(0.), v.x*phong(ld, normal, rd, col_boule+.3));

    float damier = smooth_damier(st*4.);

    col += v.y*1.*max(vec3(0.), v.x*phong(ld, normal, rd, vec3(damier*.4)*v.x));


    float dotLN_damier = clamp(dot(ld, vec3(0.,0.,1.)), 0., 1.)*(1.-v.x);
    float dotRV_damier = clamp(dot(reflect(ld, vec3(0.,0.,1.)*.8), -rd), 0., 1.)*(1.-v.x);
    col += .5*damier* pow(dotRV_damier*.99, 10.) + .4*damier * dotLN_damier;

    // Output to screen
    fragColor = vec4(col*.8, 1.0);
}

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float a = atan(uv.y, uv.x);
    vec2 p = cos(a + iTime) * vec2(cos(0.5 * iTime), sin(0.3 * iTime));
    vec2 q = (cos(iTime)) * vec2(cos(iTime), sin(iTime));

    float d1 = length(uv - p);
    float d2 = length(uv - 0.);

    vec2 uv2 = 2. * cos(log(length(uv))*0.25 - 0.5 * iTime + log(vec2(d1,d2)/(d1+d2)));///(d1+d2);
    //uv = mix(uv, uv2, exp(-12. * length(uv)));
    //uv = uv2;

    vec2 fpos = fract(4. *  uv2) - 0.5;
    float d = max(abs(fpos.x), abs(fpos.y));
    float k = 5. / iResolution.y;
    float s = smoothstep(-k, k, 0.25 - d);
    vec3 col = vec3(s, 0.5 * s, 0.1-0.1 * s);
    col += 1./cosh(-2.5 * (length(uv - p) + length(uv))) * vec3(1,0.5,0.1);

    float c = cos(10. * length(uv2) + 4. * iTime);
    col += (0.5 + 0.5 * c) * vec3(0.5,1,1) *
           exp(-9. * abs(cos(9. * a + iTime) * uv.x
                       + sin(9. * a + iTime) * uv.y 
                       + 0.1 * c));

    fragColor = vec4(col,1.0);
}

