////////////////////////////////////////////////////////////////////////////////
//
// Inverse Trig Accuracy II, mla 2022.
//
////////////////////////////////////////////////////////////////////////////////
//
// Inverse trig function errors.

// Red channel:   absolute error in acos(cos(x))
// Green channel: absolute error in asin(sin(x))
// Blue channel:  absolute error in atan(tan(x))
//
// This time, the inverse functions are computed by accurate functions,
// using a Chebyshev approximation for atan.
//
// sin and cos also calculated by accurate functions, press 'x' to use the
// native functions - be warned, the result on Intel isn't pretty.
//
// Horizontal scale is 0../2. Vertical scale is 1e-6 on all platforms.
//
// Numeric display shows the main trig functions evaluated at the mouse position
// and compared with the more accurate myxxx functions. We also compute the inverse
// functions for comparison with the original theta.
//
//         mycos(theta)         mysin(theta)         mytan(theta)
//         cos(theta)           sin(theta)           tan(theta)
// theta   myacos(mycos(theta)) myasin(mysin(theta)) myatan(mytan(theta))
//
////////////////////////////////////////////////////////////////////////////////

// atan, asin, acos using Chebshev approximation
float a = 0.0, b = 1.0;
#if 1
int N = 0;
float myatan(float x, int n) {
  // Chebyshev approximation for atan((1+x)/(1-x))
  x = (x-1.0)/(x+1.0);
  assert(x >= -1.0000000 && x <= 1.0000000);
  float x2 = x*x;
  float t = -0.0039602572;
  t = x2*t + 0.021509254;
  t = x2*t + -0.055381698;
  t = x2*t + 0.096016564;
  t = x2*t + -0.13892041;
  t = x2*t + 0.19943081;
  t = x2*t + -0.33329538;
  t = x2*t + 0.99999925;
  t = x*t + 0.78539816;
  return t;
}

#elif 1
float c[] = float[](
  0.8284271247,-0.04737854124,0.004877323528,-0.0005977260152,
  7.976388858e-05,-1.119707976e-05,1.625558989e-06, -2.417149193e-07 
);
const int N = c.length();

// Code from Numerical Recipes, adapted to use only odd coefficients.
// I don't understand Clenshaw's Recurrence (yet) so I've just made
// two copies of the loop body. Simplifications are possible.
float eval(float x, int m) {
  float d = 0.0, dd = 0.0;
  assert(m <= N);
  assert((x-a)*(x-b) <= 0.0);
  float y = (2.0*x-a-b)/(b-a);
  for (int j = m-1; j >= 0; j--) {
    float sv = d;
    d = 2.0*y*d-dd;
    dd = sv;
    sv = d;
    d = 2.0*y*d-dd+c[j];
    dd = sv;
  }
  return y*d-dd;
}

// Chebyshev series is for atan(1/(1-x)) - /4 to make domain [0,1] rather than [0,)
// and to make it an odd function in interval [0,1].
float myatan(float x, int m) {
  if (key(CHAR_Y)) return atan(x);
  return eval(x/(1.0+x),m) + 0.25*PI;
}
#else
// Simpler version using symmetry of atan about x=1, but less accurate for
// same number of coefficients, for some reason.
float c[] = float[](
  0.8541571728, 0.3947364539, -0.03545533837, -0.001940941448,
  0.001095092532, -0.0001026183744, -1.896686898e-05, 6.367123996e-06
);
const int N = c.length();

// Code from Numerical Recipes using 'Clenshaw's Recurrence'.
float eval(float x, int m) {
  float d = 0.0, dd = 0.0;
  assert(m <= N);
  assert((x-a)*(x-b) <= 0.0);
  float y = (2.0*x-a-b)/(b-a);
  for (int j = m-1; j > 0; j--) {
    float sv = d;
    d = 2.0*y*d-dd+c[j];
    dd = sv;
  }
  return y*d-dd+0.5*c[0];
}

// Since atan(PI/2-x) = 1/atan(x) we can just use the Chebyshev 
// approximation on [0,1] and flip the result where necessary.
float myatan(float x, int m) {
  if (key(CHAR_Y)) return atan(x);
  bool flip = x > 1.0;
  if (flip) x = 1.0/x;
  x = eval(x,m);
  if (flip) x = 0.5*PI-x;
  return x;
}
#endif
// acos and asin seem to be hard to do directly as a Chebshev approximation
// but fortunately they have nice definitions in terms of atan.
float myacos(float x, int m) {
  if (x == 0.0) return 0.5*PI; // Hum!
  return myatan(1.0/(x*inversesqrt(1.0-x*x)),m);
}
float myasin(float x, int m) {
  if (x == 1.0) return 0.5*PI;
  return myatan(x*inversesqrt(1.0-x*x),m);
}

// mycos and mysin have been checked on CPU & agree to a few ulps with
// builtin functions in range [0..pi/2] (ulp error is larger when result is
// closer to zero, but absolute error is fine). It's possibly worth using
// sin(pi/2-x) for cos(x) in upper half of range, but there isn't a lot in it.

float mycos(float x) {
  if (key(CHAR_X)) return cos(x);
  int N = 12;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n-1));
  }
  return y;
}


float mysin(float x) {
  if (key(CHAR_X)) return sin(x);
  int N = 10;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n+1));
  }
  return x*y;
}

// https://www.shadertoy.com/view/4sBfRd
vec2 char(vec2 p, int c) {
  // Compute uv for font texture.
  // p is [0,1] coordinates in square character
  return (p + vec2(c,15-c/16))/16.0;
}

float hud(vec2 fragCoord, float theta) {
  const int digits = 7;
  const int nrows = 15, ncols = 4;
  // Table structure
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  int column = cellindex.x;
  int row = cellindex.y;
  vec2 p = mod(fragCoord,cellsize)/fsize;
  // Get coords for mipmap scaling - p and uv have discontinuities
  vec2 uvscale = 0.5*fragCoord/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = dFdx(uvscale), dy = dFdy(uvscale); // Calculate early
  float x;
  float cost = cos(theta);
  float mycost = mycos(theta);
  float sint = sin(theta);
  float mysint = mysin(theta);
  float mytant = mysint/mycost;
  if (column == 0) {
    if (row == 0) x = theta;
    else return 0.0;
  } else if (column == 1) {
    if (row == 1) x = cost;
    else if (row == 2) x = mycost;
    else if (row == 0) x = myacos(mycost,N);
    else return 0.0;
  } else if (column == 2) {
    if (row == 1) x = sint;
    else if (row == 2) x = mysint;
    else if (row == 0) x = myasin(mysint,N);
    else return 0.0;
  } else if (column == 3) {
    if (row == 1) x = tan(theta);
    else if (row == 2) x = mytant;
    else if (row == 0) x = myatan(mytant,N);
    else return 0.0;
  } else {
    return 0.0;
  }

  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = floatchar(x,digits,charpos); // Get character at charpos, 0 if out of range
  if (c < 0) return 0.0;
  float charstart = charwidth*float(charpos); // character start
  uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  vec4 t = textureGrad(iChannel0,uv,dx,dy);
  return t.x;  
}

bool comp(float y, float r) { return (0.0 < y) == (y < r); }

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float xmouse = 0.1*iTime;
  float scale = 1e-6;
  if (iMouse.x > 10.0) {
    xmouse = iMouse.x/iResolution.x;
    //scale = exp(-10.0*iMouse.y/iResolution.y);
  }
  xmouse = 0.5*PI*fract(xmouse);
  //float check = max(0.1,-iTime);
  //if (abs(cos(check)-mycos(check)) < 1e-6) scale = 1e-6;
  float x = 0.5*PI*fragCoord.x/iResolution.x;
  float y = scale * (2.0*fragCoord.y-iResolution.y)/iResolution.y;
  float cosx = mycos(x), sinx = mysin(x), tanx = sinx/cosx;
  if (key(CHAR_B)) {
    for (int i = 0; i < 1000; i++) {
      x = myatan(mysin(x)/mycos(x),N);
    }
  }
  float t0 = x-myacos(cosx,N);
  float t1 = x-myasin(sinx,N);
  float t2 = x-myatan(tanx,N);
  vec3 col = vec3(comp(y,t0),comp(y,t1),comp(y,t2));

  col = mix(col,vec3(1),hud(fragCoord,xmouse)); 
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

