const float PI = 3.141592654;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

// A couple of Chebyshev approximations
// Defined on domain [-1,1] so do PI/2-f(1/x) outside.
float f0(float x) {
  assert(x >= -1.0000000 && x <= 1.0000000);
  float x2 = x*x;
  float t = -0.038254465;
  t = x2*t + 0.14498249;
  t = x2*t + -0.32053329;
  t = x2*t + 0.99913345;
  t = x*t;
  return t;
}

float f(float x) {
  assert(x >= -1.0000000 && x <= 1.0000000);
  float x2 = x*x;
  float t = 0.020419555;
  t = x2*t + -0.084198465;
  t = x2*t + 0.17944049;
  t = x2*t + -0.33010496;
  t = x2*t + 0.99985132;
  t = x*t;
  return t;
}

float myatan0(float x) {
  bool flip = abs(x) > 1.0;
  if (flip) x = 1.0/x;
  x = f(x);
  if (flip) return 0.5*PI-x;
  return x;
}

