// Wrapper for division to allow emulation of GPU style division
// on CPU, not that this makes much difference to result.
float fdiv(float x, float y) { return x/y; }

// If we've found the character for the target index, return it.
#define CHAR(c) { if (index++ == cindex) return(c); }
#define SIGN(x) { if ((x) < 0.0) CHAR(MINUS) else CHAR(SPACE) } 

// Format x to digits precision and return the character at cindex,
// or -1 if cindex is out of range.
int floatchar(float x, int digits, int cindex) {
  if (cindex < 0 || cindex > digits+6) return -1;
  int index = 0;
  const float pmax = 1e15; //  Large power of 10 with a nice representation
  const int emax = 15; // Exponent of pmax
  const float pow10[] = float[]( 1e1,1e2,1e3,1e4,1e5,1e6,1e7,1e8 );
  const int SPACE = 0x20, ZERO = 0x30, POINT = 0x2e, MINUS = 0x2d, PLUS = 0x2b;
  const int A = 0x61, E = 0x65, F = 0x66, I = 0x69, N = 0x6e;
  if (isinf(x)) {
    SIGN(x);
    CHAR(I); CHAR(N); CHAR(F);
  } else if (isnan(x)) {
    // Maybe show the bits
    if (floatBitsToInt(x) < 0) CHAR(MINUS);
    CHAR(N); CHAR(A); CHAR(N);
  } else if (x == 0.0) {
    SIGN(x);
    CHAR(ZERO);
  } else {
    SIGN(x);
    x = abs(x);
    int exp = 0; // Compute exponent here
    float llimit = pow10[digits-2], ulimit = 10.0*llimit;
    // Now put x in the range llimit <= x <= ulimit
    // This is a delicate business if we aren't to
    // lose too much precision
    while (x > llimit*pmax) { x /= pmax; exp += emax; }
    while (x < fdiv(ulimit,pmax)) { x *= pmax; exp -= emax; }
    if (x >= ulimit) {
      float p = 1.0;
      while (fdiv(x,p) > ulimit) {
        exp++; p *= 10.0;
      }
      x = fdiv(x,p);
    } else if (x < llimit) {
      float p = 1.0;
      while (x*p < llimit) {
        exp--;  p *= 10.0;
      }
      x *= p;
    }
    assert(!isinf(x));
    assert(!isnan(x));
    assert(x >= llimit);
    assert(x <= ulimit); // Equality is possible!
    // Round to an integer which has exactly the significant figures we want.
    int n = int(round(x));
    if (n == int(ulimit)) {
      // Just in case the round has taken us the wrong way
      n /= 10; exp++;
    }
    assert(n >= int(llimit));
    assert(n < int(ulimit));
    // Now we have a correctly rounded integer representation together
    // with the correct exponent. The rest is just formatting.

    // See if we want to use exponent notation
    int e = 0;
    if (exp <= -digits-4) {
      e = exp+digits-1;
      exp = -digits+1;
    } else if (exp > 0) {
      e = exp+digits-1;
      exp = -digits+1;
    }
    while(n%10 == 0 && exp < 0) {
      // Remove trailing zeros
      n /= 10; exp++;
    }
    int m = 1, i = exp+1;
    while (10*m <= n) { m *= 10; i++; }
    // i is number of characters in front of decimal point
    if (i <= 0) {
      CHAR(ZERO);
      CHAR(POINT);
      // Add leading digits for 0 < x < 1
      while(i < 0) {
        CHAR(ZERO); i++;
      }
    }
    while(m != 0) {
      CHAR(ZERO+n/m%10);
      m /= 10; i--;
      // Don't print trailing point!
      if (i == 0 && m != 0) CHAR(POINT);
    }
    if (e != 0) {
      CHAR(E);
      CHAR(e < 0 ? MINUS : PLUS);
      e = abs(e);
      CHAR(ZERO+e/10); CHAR(ZERO+e%10); 
    }
  }
  return -1;
}

