int     len = 20;  // curve length ( i.e. number of samples )
float Nvort = 30., // number of vortices 
      Nline = 80.;

vec2 R; // can't #define because used in field (via H) but Common is stupidely not an include

#define T(U)   texelFetch( iChannel0, ivec2(U), 0 )
#define H(p) ( fract(sin(mod(float(p)*vec2(269.5,183.3),6.283)) *1e4)*R/R.y )
#define hue(v)( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

float L(vec2 p, vec2 a,vec2 b) { // --- draw line  ( squared distance )
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b), 
          c = clamp(h, 0., 1.);     
    return h==c ? dot(p -= b*h ,p) : 1e5; 
}

vec4 C = vec4(0);  // returns color field

vec2 field(vec2 U, sampler2D ch) {  // --- velocity field induced by vortices ( Biot & Savart )
    vec2 V = vec2(0), P;
    float w;
    for( float i=0.; i < Nvort; i++ ) { // N random vortices ( same strength )
        P = texelFetch(ch,ivec2(i,1),0).xy - U; // don't use ',' or bug on Windows !
        w = min( 1., .001 / pow(length(P),3.) ), 
        C += hue(i/Nvort)    * w ,        // colored influence
        V += vec2(-P.y,P.x)  * w          // velocity influence
                 // * sign(H(i+.3).y-.5)  // random sign
                 // * (2.*H(i+.3).y-1.)   // random sign & strength
                 // * H(i+.3).y*2.        // random strength
                          // * min(100.,.2/dot(P,P) )
                ;}
    return V*.1;
}

