float Noise2d( in vec2 x )// https://www.shadertoy.com/view/Md2SR3 cop 
{
    float xhash = cos( x.x * 21.0 );
    float yhash = cos( x.y * 3.0 );
    return fract( 245.92653 * ( xhash + yhash ) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 iViewportSize = vec2(iResolution.xy);
    vec2 viewCoords = vec2(gl_FragCoord.x, iViewportSize.y - gl_FragCoord.y);
    float iRadius = 1.5;

    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    vec2 iCenter = (vec2(734.0))  ;      
    float r = Noise2d(iCenter - viewCoords);
    float d = length(r * iViewportSize) - iRadius * abs(cos(iTime)*.750) + 2.0 * abs(sin(iTime)*0.005);
    float t = smoothstep(.0,iRadius,d) ;
    vec4 color = vec4(.5,1.0,.4,1.0);
    fragColor = vec4(color.rgb * 1.0-(t),color.a * 1.0-(t));
}

