uint read(vec2 u, vec2 m)
{
    vec2 o = mod(u+m,iResolution.xy);
         u = floor(u/iResolution.xy)*iResolution.xy+o;
    return floatBitsToUint(texelFetch(iChannel0,ivec2(u),0).x);
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    float ck = -floor((sz-1.)*.5);    //center the kernel
    float ev = float(mod(sz,2.)==0.); //move and resize kernel when sz = even
    uint o = 0U;                      //sum of all bits
                          float n = (float(iFrame&1)-1.)*ev;
    for(int   t=0 ; t<tz; ++t){ float m = n*(1.-float(t&1)); float s2 = sz-float(t&1)*ev; 
    for(float i=0.; i<s2; ++i){
    for(float j=0.; j<s2; ++j){ uint r = read(u,vec2(i,j)+ck+m);
                                o += (r>>t)&1U;
    }}}                         o += (read(u,vec2(0))>>2)&1U;

    uint a = +6U          //change to 7U for clean wiglers
             +16U*8U*8U;
    o = (read(u,vec2(0,0))<<1U) | ((a>>(o&31U))&1U);

    vec2 v = (u        *2.-iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy*2.-iResolution.xy)/iResolution.y-v;
    if(iFrame==0)
    {
        float r = fract(cos(dot(u,vec2(1.76543,iTime+9.)))*5467.5678)*float(1<<tz);
              r*= step(dot(v,v),nz);
        o = uint(r+1.);
    }
    if(iMouse.z>.5 && dot(m,m)<.005)
    {
        float r = fract(cos(dot(u,vec2(1.76543,iTime+9.)))*5467.5678)*float(1<<tz);
        o = uint(r);
    }
    fragColor = vec4(uintBitsToFloat(o));
}

