// Adapted from
// https://www.shadertoy.com/view/XdsGDr

#define ITER 120

vec2 mir( vec2 pix, float zoom)
{
	vec2 z = zoom * pix;    
    z.x = -abs(z.x) + .3;
    return z;
}

#define S smoothstep
vec3 colMap( float x )
{
	vec3 col = vec3(0.);
	col.x = S(.0, .3, x) - .75*S(.2,.9,x);
	col.y = S(.0, .4, x) - .75*S(.3,.8,x);
	col.z = S(.7, .8, x) - .85*S(.8,.9,x);

	return col * vec3(1.6, .8, .8) * vec3(1., 1.15, 1.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy,
        tc = ( fragCoord - .5*R ) / R.y;

	//vec2 m = iMouse.xy / R.xy;
	float t = iTime * .5;

    float zoom = 1.5 + 1.*(.5*cos(.15*t)+.5);
    float freq = 4.;

    // Animate Julia params
    vec2 c = vec2(-0.35, 0.7);
    c += vec2(
        .1 + .016*cos(t*freq),
        -.005+-(.0123*sin(t*freq)*sin(t*freq*1.1))
        );

	vec2 z = mir(tc, zoom);
    vec2 z0 = z;
    vec2 eye = vec2(-.31, .41);

    float m = clamp(0.,1., length(z-eye)*3.);
    c = mix(c, c*.94, 1.-m*m*m);

    // Julia
	int i = 0;
	for(; i<ITER; i++)
    {
		float x = (z.x * z.x - z.y * z.y) + c.x;
		float y = (z.y * z.x + z.x * z.y) + c.y;

		if((x * x + y * y) > 4.0)
        {
			break;
		}
		z.x = x;
		z.y = y;
	}

    float q = 40. + 10.*sin(.1*t);
    float x = fract(float(i) / q);
	if(i == ITER)
    {
        //iris
        x = sin(100.*length(z0-eye) - 20.*t);
	}
	vec3 col = colMap(x);

	fragColor.xyz = col;
}

