#define PI 3.1415927

// https://kaegi.nl/werner/userfiles/downloads/vosim-system.pdf
float vosim(in float time, in float freq, in float n)
{
    float m = 3.;
    float ph = mod(freq*time, 1.);
    float fund = sin(n*PI*ph);
    fund *= 1.-ph;

    return fund * fund;
}

vec2 mainSound( in int samp,float time)
{
    float vol = 1.;

    float n = 6.;
    // modulate n
    float n1 = n + 1.*sin(time);
    float n2 = n + 1.*sin(1.1*time);

    // Chant
    float m = 3.;
    float chant = 0.;
    float low = 55.;
    chant += vosim(time, low, n1);
    chant -= vosim(time, low*3.0/2., n2);
    chant -= vosim(time, low*1.006, n2);
    chant += vosim(time, low*3.0055/2., n1);
    chant *= .06125;

    float sig = 0.;
    sig += .5*chant;

    // Elf says "ohhh woooowwwww"
    float ooo = 0.;
    float n3 = n + 4.*cos(4.*time+5.);

    // modulate freq
    float x = 4.*time + 95.;
    float pmod = sin(x) + sin(1.3*x) + sin(1.7*x);
    time += .2*.13*pmod;
    float freq = 180.;

    ooo += vosim(time, freq, n3);

    x = .5*time;
    float vmod = sin(1.23*x) + sin(1.61*x) + sin(2.1*x);
    //vmod = sin(1.23*x);
    vmod = pow(clamp(abs(sin(x))-.4, 0.,1.),.12);

    float t = time * .5;
    float near = .125*(.5*cos(.15*t)+.5);
    sig += (.25-near)*vmod*vmod * ooo;

    return vol * vec2(sig);
}

