/** 
    License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

    Cube Field / DOF experiment
    11/08/22 | byt3_m3chanic

    Trying to use a bayer texture and some tricks picked up from @Nusan to
    do this DOF thing..more experiment than accuracy

    left = bayer | right = blue noise
    @Xor suggestion for blue noise..
*/


#define R     iResolution
#define T     iTime
#define M     iMouse
#define PI    3.14159265358
#define PI2   6.28318530718

mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 a){ return fract(sin(dot(a,vec2(22.34,35.34)))*483434.);}
vec2 hash22(vec2 uv) {return fract(sin(uv*425.215+uv.yx*714.388)*vec2(522.877));}

float box(vec3 p, vec3 a){
    vec3 q = abs(p)-a;
    return length(max(q,0.))-min(max(q.x,max(q.z,q.y)),0.);
}

const float scale = 2.;
const vec2 s = vec2(scale*2.);

float pf=0.,spf=0.,gpf=0.,hsh=0.,gsh=0.,glow=0.,sglow=0.;
vec3 hit=vec3(0),hp=vec3(0);

vec2 map(vec3 q3) {
    vec2 res=vec2(1e5,0.);

    float yd=floor((q3.y+3.)/6.);
    q3.y=mod(q3.y+3.,6.)-3.;
    q3.z+=T*1.5;

    // Multi Tap loop @Shane https://www.shadertoy.com/view/WtffDS
    vec2 p,ip,ct = vec2(0);
    const vec2[4] ps4 = vec2[4](vec2(-.5,.5),vec2(.5),vec2(.5,-.5),vec2(-.5));
    float d1=1e5;

    for(int i = 0; i<4; i++) {

        ct = ps4[i]/2. -  ps4[0]/2.;
        p = q3.xz - ct*s;
        ip = floor(p/s) + .5;
        p -= (ip)*s;	   
        vec2 idi = (ip + ct)*s;

        vec3 q = vec3(p.x,q3.y,p.y);

        float hs=hash21(idi+yd);
        float gs=hash21(idi+yd+floor(T*2.));

        //@Nusan - mixing states from twitter..
        pf = mix(hash21(vec2(floor(T)+hs)), hash21(vec2(floor(T)+1.+hs)), pow(smoothstep(0.,1.,fract(T)), 8.));
        pf = clamp(pf*.45,.15,1.);

        float d = box(q,vec3(scale*pf))-.075;
        d=max(d,-(box(q,vec3(scale*pf*.8))-.075));

        float thk = (scale*.7*pf),thx = (scale*.03);
        float pipe = min(min(length(q.xz)-thk,length(q.yz)-thk),length(q.yx)-thk);
        float xipe = min(length(q.yx)-thx,length(q.yz)-thx);

        if(xipe<res.x) {
            res=vec2(xipe,2.);
            gsh=hs;
            hit=q;
            gpf=pf;
        }

        if(hs<.9) d=max(d,-pipe);

        if(d<res.x) {
            res=vec2(d,1.);
            hit=q;
            gsh=hs;
            gpf=pf;
        }

        float d4 = length(q)-(scale*.35*pf);
        if(gs>.68&&hs<.9) glow+=.004/(.0025+d4*d4);
        if(d4<res.x&&gs>.68) {
            res=vec2(d4,2.);
            gsh=hs;
        }
    }
    return res;
}

vec3 normal(vec3 p, float t){
    vec2 e=vec2(t*1e-3,0.);
    float d = map(p).x;
    vec3 n = d-vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x
    );
    return normalize(n);
}

// Tri-Planar blending function. Ryan Geiss: 
// https://developer.nvidia.com/gpugems/GPUGems3/gpugems3_ch01.html
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n ){
    p*=.175;
    p+=hsh*5.;
    p.xz*=rot(hsh*PI2+.38);
    n = max(abs(n), 1e-3);
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;
    return mat3(tx*tx, ty*ty, tz*tz)*n;
}

vec3 hue(float a){return .33+.66*sin(PI2*a*vec3(.95,.48,.08)*vec3(1,.98,.95));}

void mainImage( out vec4 fragColor, in vec2 F )
{
	vec2 uv = (2.*F.xy - R.xy)/max(R.x,R.y);
    vec2 vv=uv;
    uv*=rot(-.38*sin(T*.1));
	vec3 C = vec3(0);

    vec3 ro = vec3(0,0,6), rd=normalize(vec3(uv,-1.));

    float dof = 0.001;
    float dofdist = 1.0/6.;
    vec2 off=vec2(.05);

    float y = M.xy == vec2(0) ? 0. : (M.x/R.x * 2. - 1.) * PI;     
    mat2 rx = rot(-.72),ry = rot(T*.05+y);
    ro.zy*=rx;ro.xz*=ry;
    rd.zy*=rx;rd.xz*=ry;

    float fa=0.;
    for(int k=0;k<2;k++){

        vec3 p = ro;
        float d = 0., m = 0.;


        vec3 RC = vec3(0);
        for(int i=0;i<86;i++){

            //modified jitter/dof 
            //inspiration @Nusan https://www.shadertoy.com/view/3sXyRN
            if(mod(float(i),2.)<1.){
                off= vv.x>.0? texture(iChannel1,F.xy/1024.).rg*2.-1.: texture(iChannel2,F.xy/8.).rg*2.-1.;
                vec2 fp = off*dof;
                ro.xz+= vv.x>.0? fp*(d)*.002: fp*(d)*.02;
                rd.xy+= vv.x>.0? fp*(d*d)*dofdist*.02: fp*(d*d)*dofdist*.04;
            }

            p=ro+d*rd;

            vec2 ray = map(p);
            m=ray.y;
            d+=i<20?ray.x*.5:ray.x*.9;
            if(ray.x<d*1e-4||d>30.)break;
        }

        hsh=gsh;
        spf=gpf;
        hp=hit;

        if(k==0) {fa=d;sglow=glow;}

        if(d<30.) {
            vec3 n = normal(p,d);
            vec3 l = normalize(vec3(1,14,-5));
            float diff = clamp(dot(n,l),0.1,1.);

            vec3 h = hue(73.+hsh);
            h=mix(tex3D(iChannel0,hp/spf,n).rgb,h,.05);
            if(m==2.) h = vec3(.1);
            RC=diff*h;

            ro = p+n*.001;
            rd = reflect(rd,n);

        }

        if(k>0) RC *=.125;

        C = clamp(C+RC,vec3(0),vec3(1));

    }

    C=mix(C,vec3(sglow,sglow*.5,sglow*.15),clamp(sglow,0.,.2));
    C=mix(C,mix(vec3(.12,.08,.06),vec3(.9,.69,.39),clamp((uv.y+.5)*.5,0.,1.)),1.-exp(-.0003*fa*fa*fa));
    if(vv.x>-.002&&vv.x<.002)C=vec3(1);
    C=pow(C,vec3(.4545));
	fragColor = vec4(C,1.);
}

////////////////////////////////////////////////////////////////////////////////
//
// Inverse Trig Function Accuracy, mla 2022.
//
////////////////////////////////////////////////////////////////////////////////
//
// Inverse trig function errors.

// Red channel:   absolute error in acos(cos(x))
// Green channel: absolute error in asin(sin(x))
// Blue channel:  absolute error in atan(tan(x))
//
// sin and cos calculated by accurate functions, press 'x' to use the
// native functions - be warned, the result on Intel isn't pretty.
//
// Note that acos and asin are significantly less accurate than atan
// (on Nvidia, Pixel 4a and Intel anyway, very much so on Intel).
//
// Horizontal scale is 0../2. Vertical scale is 1e-4 on all platforms.
//
// Numeric display shows the main trig functions evaluated at the mouse position
// and compared with the more accurate myxxx functions. We also compute the inverse
// functions for comparison with the original theta.
//
//         mycos(theta)       mysin(theta)       mytan(theta)
//         cos(theta)         sin(theta)         tan(theta)
// theta   acos(mycos(theta)) asin(mysin(theta)) atan(mytan(theta))
//
////////////////////////////////////////////////////////////////////////////////

// mycos and mysin have been checked on CPU & agree to a few ulps with
// builtin functions in range [0..pi/2] (ulp error is larger when result is
// closer to zero, but absolute error is fine). It's possibly worth using
// sin(pi/2-x) for cos(x) in upper half of range, but there isn't a lot in it.

float mycos(float x) {
  int N = 12;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n-1));
  }
  return y;
}


float mysin(float x) {
  int N = 10;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n+1));
  }
  return x*y;
}

// https://www.shadertoy.com/view/4sBfRd
vec2 char(vec2 p, int c) {
  // Compute uv for font texture.
  // p is [0,1] coordinates in square character
  return (p + vec2(c,15-c/16))/16.0;
}

float hud(vec2 fragCoord, float theta) {
  const int digits = 7;
  const int nrows = 15, ncols = 4;
  // Table structure
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  int column = cellindex.x;
  int row = cellindex.y;
  vec2 p = mod(fragCoord,cellsize)/fsize;
  // Get coords for mipmap scaling - p and uv have discontinuities
  vec2 uvscale = 0.5*fragCoord/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = dFdx(uvscale), dy = dFdy(uvscale); // Calculate early
  float x;
  float cost = cos(theta);
  float mycost = mycos(theta);
  float sint = sin(theta);
  float mysint = mysin(theta);
  float mytant = mysint/mycost;
  if (column == 0) {
    if (row == 0) x = theta;
    else return 0.0;
  } else if (column == 1) {
    if (row == 1) x = cost;
    else if (row == 2) x = mycost;
    else if (row == 0) x = acos(mycost);
    else return 0.0;
  } else if (column == 2) {
    if (row == 1) x = sint;
    else if (row == 2) x = mysint;
    else if (row == 0) x = asin(mysint);
    else return 0.0;
  } else if (column == 3) {
    if (row == 1) x = tan(theta);
    else if (row == 2) x = mytant;
    else if (row == 0) x = atan(mytant);
    else return 0.0;
  } else {
    return 0.0;
  }

  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = floatchar(x,digits,charpos); // Get character at charpos, 0 if out of range
  if (c < 0) return 0.0;
  float charstart = charwidth*float(charpos); // character start
  uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  vec4 t = textureGrad(iChannel0,uv,dx,dy);
  return t.x;  
}

bool comp(float y, float r) { return (0.0 < y) == (y < r); }

// Possibly better implementations of acos and asin.
float acos2(float x) { return atan(1.0/(x*inversesqrt(1.0-x*x))); }
float asin2(float x) { return atan(x*inversesqrt(1.0-x*x)); }

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float xmouse = 0.1*iTime;
  float scale = 1e-4;
  if (iMouse.x > 10.0) {
    xmouse = iMouse.x/iResolution.x;
    //scale = exp(-10.0*iMouse.y/iResolution.y);
  }
  xmouse = 0.5*PI*fract(xmouse);
  //float check = max(0.1,-iTime);
  //if (abs(cos(check)-mycos(check)) < 1e-6) scale = 1e-6;
  float x = 0.5*PI*fragCoord.x/iResolution.x;
  float y = scale * (2.0*fragCoord.y-iResolution.y)/iResolution.y;
  float cosx = mycos(x), sinx = mysin(x), tanx = sinx/cosx;
  if (key(CHAR_X)) {
    cosx = cos(x), sinx = sin(x), tanx = tan(x);
  }
  float t0 = x-acos(cosx);
  float t1 = x-asin(sinx);
  float t2 = x-atan(tanx);
  vec3 col = vec3(comp(y,t0),comp(y,t1),comp(y,t2));

  col = mix(col,vec3(1),hud(fragCoord,xmouse)); 

  fragColor = vec4(col,1);
}

