float random(vec3 p){
    return fract((sin(p.x*203.24 + 20398.243)*sin(p.y*834.29 + 2382.2033)*sin(p.z*405.09))*20384.043);
}

float noise2(vec2 p){
    vec2 luv = fract(p);
    vec2 id = floor(p);
    luv = luv*luv*(3.0 - 2.0*luv);
    float bl = random(vec3(id, 1));
    float br = random(vec3(id + vec2(1, 0), 1));
    float b = mix(bl, br, luv.x);

    float tl = random(vec3(id + vec2(0, 1), 1));
    float tr = random(vec3(id + vec2(1, 1), 1));
    float t = mix(tl, tr, luv.x);
    return mix(b, t, luv.y);
}

float noise3(vec3 p){
    vec3 fc = floor(p);
    vec3 frc = fract(p);
    frc = smoothstep(0.0, 1.0, frc);

    float tlf = random(fc + vec3(0, 1, 0));
    float trf = random(fc + vec3(1, 1, 0));
    float blf = random(fc + vec3(0, 0, 0));
    float brf = random(fc + vec3(1, 0, 0));
    float tlb = random(fc + vec3(0, 1, 1));
    float trb = random(fc + vec3(1, 1, 1));
    float blb = random(fc + vec3(0, 0, 1));
    float brb = random(fc + vec3(1, 0, 1));

    float lerpTopFront = mix(tlf, trf, frc.x);
    float lerpBottomFront = mix(blf, brf, frc.x);

    float lerpTopBack = mix(tlb, trb, frc.x);
    float lerpBottomBack = mix(blb, brb, frc.x);

    float lerpFront = mix(lerpBottomFront, lerpTopFront, frc.y);
    float lerpBack = mix(lerpBottomBack, lerpTopBack, frc.y);

    return mix(lerpFront, lerpBack, frc.z);
}


SDF sphere(vec3 p, float rad, material mat, int index){
    return SDF(length(p) - rad, mat, index);
}

SDF boxSDF(vec3 p, vec3 s, material mat, int index){
    vec3 q = abs(p) - s;
    return SDF(length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0), mat, index);
}

SDF yPlane(vec3 p, float y, material mat, int index){
    return SDF(p.y - y, mat, index);
}

SDF boxFrameSDF(vec3 p, vec3 s, float sw, float ro, material mat, int index){
  p = abs(p) - s;
  vec3 q = abs(p + sw) - sw;
  return SDF(min(
      min(
          length(max(vec3(p.x, q.y, q.z),0.0)) + min(max(p.x, max(q.y, q.z)), 0.0),
          length(max(vec3(q.x, p.y, q.z),0.0)) + min(max(q.x, max(p.y, q.z)), 0.0)
      ),
      length(max(vec3(q.x, q.y, p.z), 0.0)) + min(max(q.x, max(q.y, p.z)), 0.0)) - ro, mat, index);
}

SDF sceneSDF(vec3 p, float type){

    /*SDF[] scene = SDF[](
