    float wn = noise3((p + vec3(5, iTime*wv, 2))*wf)*wh;
    for(int i = 0; i < wd; i++){
        wh /= 2.0;
        wf *= 2.0;
        wv *= 2.0;
        wn += noise3((p + vec3(5.0 + float(i), iTime*wv + float(i), 2.0 + float(i)))*wf)*wh;
    }

    wn /= 2.0;

    //SDF s1 = sphere(p - lPos, 1.0, material(vec3(1), 0.0, 0.9, 0.0), 0);
    //SDF s2 = sphere(p - vec3(-1, 1.0, 3), 0.6, material(vec3(0, 1, 1), 3.0, 0.6, 0.0), 1);
    SDF s3 = sphere(p - vec3(0, sin(-iTime*10.0)*0.03, 0), 0.25, material(vec3(0.6, 0.4, 0.2), 1.0, 0.7, 0.0), 1);
    SDF s4 = yPlane(p, 0.3 - mix(sin(length(p - vec3(0, 0.4, 0))*10.0 - iTime*10.0)*0.01, 0.0, min(length(p - vec3(0, 0.4, 0))/3.0, 1.0)), material(vec3(0, 0.05, 0.1), 3.0, 0.8, 0.0), 2);

    s3.dist -= gn;

