    }
}

bool raymarch(float type, vec3 o, vec3 d, out float t, float eps, out material mat, out int index){
    t = 0.0;
    for(int i = 0; i < maxSteps && t <= 30.0; i++){
        SDF s = sceneSDF(o + d*t, type);
        t += s.dist;
        mat = s.mat;
        index = s.index;
        if(s.dist < eps*t*0.1 && t > 0.0){
            return true;
        }
    }
    return false;
}

vec3 getNormals(vec3 h){
    return normalize(
        vec3(
            sceneSDF(vec3(h.x + epsilon, h.yz), 0.0).dist - sceneSDF(vec3(h.x - epsilon, h.yz), 0.0).dist,
            sceneSDF(vec3(h.x, h.y + epsilon, h.z), 0.0).dist - sceneSDF(vec3(h.x, h.y - epsilon, h.z), 0.0).dist,
            sceneSDF(vec3(h.xy, h.z + epsilon), 0.0).dist - sceneSDF(vec3(h.xy, h.z - epsilon), 0.0).dist
        )
    );
}

float getLighting(vec3 h, vec3 lpos, material hitMat, int ii){
    float brightness = 1.0;

    vec3 n = getNormals(h);
    vec3 lRay = normalize(lpos - h);

    if(hitMat.type != 0.0){
        float lt;
        material lMat;
        int i;

        bool rl = raymarch(1.0, h + n*0.007, lRay, lt, 0.001, lMat, i);
        brightness = max(dot(n, lRay), 0.01);

        if(rl && i != ii){
            brightness -= 0.6;
        }
    }else{
        brightness = 1.0;
    }
    return brightness;
}

vec3 reflectRay(vec3 h, vec3 d){
    vec3 n = getNormals(h);
    return d - 2.0*n*dot(n, d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float f = 1.0/tan(FOV/2.0);

    mat3 projection;
    projection[0] = vec3(1, 0, 0);
    projection[1] = vec3(0, 1, 0);
    projection[2] = vec3(0, 0, f);

    vec3 col = fogColor;

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    camera cam = camera(vec3(2.5, 3, -6), vec3(20, 20, 0));

    mat3 xRotation;
    xRotation[0] = vec3(1, 0, 0);
    xRotation[1] = vec3(0, cos(cam.d.x*degreesToRadians), -sin(cam.d.x*degreesToRadians));
    xRotation[2] = vec3(0, sin(cam.d.x*degreesToRadians), cos(cam.d.x*degreesToRadians));

    mat3 yRotation;
    yRotation[0] = vec3(cos(cam.d.y*degreesToRadians), 0, -sin(cam.d.y*degreesToRadians));
    yRotation[1] = vec3(0, 1, 0);
    yRotation[2] = vec3(sin(cam.d.y*degreesToRadians), 0, cos(cam.d.y*degreesToRadians));

    vec3 d = normalize(vec3(uv, 1.0)*projection*xRotation*yRotation);
    vec3 o = cam.o;

    float t;
    material mat;
    int si;

    bool rs = raymarch(0.0, o, d, t, epsilon, mat, si);

    if(rs){
        mat.brightness = getLighting(o + d*t, lPos, mat, si);

        if(mat.type == 1.0){
            col = mix(mat.col*mat.brightness, fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
        }else if(mat.type == 0.0){
            col = mat.col;
        }else if(mat.type == 3.0){
            float rt;
            material rmat;
            int ri;
            vec3 rd = reflectRay(o + d*t, d);

            vec3 n = getNormals(o + d*t);

            bool rr = raymarch(0.0, (o + d*t) + n*0.02, normalize(rd), rt, epsilon, rmat, ri);

            if(rr){
                //mat.brightness = getLighting(o + d*t, lPos, mat, si);
                float reflectLighting = getLighting((o + d*t) + normalize(rd)*rt, lPos, rmat, ri);
                col = mix(mix(rmat.col*reflectLighting, mat.col*mat.brightness, mat.intensity), fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
            }else{
                col = mix(mix(fogColor, mat.col, mat.intensity)*mat.brightness, fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
            }
        }
    }

    fragColor = vec4(pow(col, vec3(1.0/2.2)), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1.0);
    // a sine wave but not horizontal
    vec2 a_n_pix_cor_nor = fragCoord.xy / iResolution.xy; 
    vec2 a_n_mou_cor_nor = iMouse.xy / iResolution.xy; 
    float n_tau = 6.283185;
    float n_mount = 0.0;
    float n_amp = 0.2;
    vec2 a_n_trans = vec2(0.0, 0.5);
    float n_y = 
        sin(a_n_pix_cor_nor.x * n_tau + a_n_trans.x)
        * n_amp
        + a_n_trans.y;

    float n_dist = abs(a_n_pix_cor_nor.y - n_y);

    // https://www.desmos.com/calculator/r9j8egdlvx
    float n_dist_modified = pow(n_dist, 0.3);
    //fragColor *= vec4(n_dist_modified);


    float n_curves = 24.0;
    float n_dist_shortest = 1.0;
    float n_curve_col = 0.0;
    for(
        float n_curve_norm = 0.0;
        n_curve_norm <= 1.0; 
        n_curve_norm += 1.0/n_curves
        ){

            n_y = 
                sin(a_n_pix_cor_nor.x * n_tau + a_n_trans.x + n_curve_norm*5.0 + iTime)
                * n_amp 
                + a_n_pix_cor_nor.x*n_curve_norm*2.0;

            n_dist = abs(a_n_pix_cor_nor.y - n_y);

            // https://www.desmos.com/calculator/r9j8egdlvx
            n_dist_modified = pow(n_dist, 0.3);

            if(n_dist_modified < n_dist_shortest){
                n_dist_shortest = n_dist_modified;
                n_curve_col = fract(sin(1234.1234*n_curve_norm)*1234.1234);
            }

            n_y = 
                sin(a_n_pix_cor_nor.x * n_tau + a_n_trans.x + n_curve_norm*5.0 + iTime)
                * n_amp 
                + 1.0-a_n_pix_cor_nor.x*n_curve_norm*2.0;

            n_dist = abs(a_n_pix_cor_nor.y - n_y);

            // https://www.desmos.com/calculator/r9j8egdlvx
            n_dist_modified = pow(n_dist, 0.3);

            if(n_dist_modified < n_dist_shortest){
                n_dist_shortest = n_dist_modified;
                n_curve_col = fract(sin(1234.1234*n_curve_norm)*1234.1234);
            }
        }

    if(1.0-a_n_pix_cor_nor.y > a_n_pix_cor_nor.x){

        fragColor *= vec4(
            n_dist_shortest, 
            n_curve_col, 
            1.-n_curve_col, 
            0.
        );
    }else{
        fragColor = vec4(n_dist_shortest);
    }



}

float f_n_random_norm(float n){
