#define R (iResolution)
//const float n_tau = 6.28318530717958647;
float f_n_difference_to_line_function(
    vec2 a_n_start, 
    vec2 a_n_end, 
    vec2 a_n_pixel_coord_normalized
){
    //remember that math lesson ?
    // y = m*x+q
    float n_delta_y = a_n_end.y - a_n_start.y; 
    float n_delta_x = a_n_end.x - a_n_start.x; 
    float n_m = n_delta_y / n_delta_x;
    float n_q = -(n_m*a_n_start.x) - a_n_start.y;

    float n_x = a_n_pixel_coord_normalized.x;

    float n_y = n_m * n_x + n_q; 

    float n_difference = a_n_pixel_coord_normalized.y - n_y;
    return n_difference;


}

//Or iq's scheme:
// https://www.shadertoy.com/view/3tdSDj
vec3 f_colorize_field_iq(float dist, vec3 color)
{
    // Change color if dist is negative.
    color = 1.0 - sign(dist) * color;

    // Attenuate color around edges.
	color *= 1.0 - exp(-4.0 * abs(dist));

    // Add interleaved waves.
	color *= 0.7 + 0.2 * cos(30.0 * dist);

    // Add white edges.
    return mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 6./R.y, abs(dist)));
}


// https://www.shadertoy.com/view/ddfGz2
vec3 f_colorize_field_Envy24(float dist, vec3 color)
{    
    float F = 30., freq = F*dist, aa = 1./F, C = 0.7, amp = 0.2, width = 0.3,
          waves_sdf = C + amp*smoothstep(8./R.y, 0., (width+cos(freq)) * aa),
          exp_raise = 1. - exp(-4.*abs(dist));

    color = 1.0 - sign(dist) * color; // Change color if dist is negative. 
    color *= exp_raise;               // Attenuate color around edges.     
    color *= waves_sdf;               // Add waves.

    return mix(color, vec3(1), smoothstep(8./R.y, 0., abs(dist)) );
}

