void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_){	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])             
        i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

vec4 colorFromId(int id)
{
    int index = id % g_NumColors;

    switch (index)
    {
    case 0: return vec4(1,0,0,0);
    case 1: return vec4(0,1,0,0);
    case 2: return vec4(0,0,1,0);
    case 3: return vec4(0,0,0,1);
    }
}

struct Demo
{
    float used;
    float size;
    float speed;
    float turn;
    int colors;
    vec3 colorR;
    vec3 colorG;
    vec3 colorB;
    vec3 colorA;
};

const int NUM_DEMOS = 14;
Demo DEMOS[NUM_DEMOS] = Demo[](
    Demo( .5,   .003, .002, 1.2, 4, vec3(.8,.3,0),   vec3(.7,.6,.3),  vec3(0,.2,.7),  vec3(.9,.3,.6)), // intro
    Demo( .01,  .01,  .01,   .5, 1, vec3(0,.4,.1),   vec3(0),         vec3(0),        vec3(0)),        // oscilloscope
    Demo( .003, .01,  .003,  .3, 2, vec3(0,1,1),     vec3(1,0,0),     vec3(0),        vec3(0)),        // branching
    Demo( .003, .01,  .003,  .9, 3, vec3(0,1,1),     vec3(1,0,0),     vec3(1, .7, 0), vec3(0)),        // looping
    Demo(1.,    .004, .005,  .5, 3, vec3(0,.2,.6),   vec3(.6,0,.3),   vec3(.2,.6,.6), vec3(0)),        // blizzard
    Demo(1.,    .004, .001, 1. , 4, vec3(.8,.7,.6),  vec3(.9,.8,.1),  vec3(.9,.5,.0), vec3(.5,0,0)),   // solar flare
    Demo( .8,   .005, .005, 1.5, 1, vec3(1,.2,.1),   vec3(0),         vec3(0),        vec3(0)),        // fire
    Demo( .4,   .01,  .001,  .5, 2, vec3(.6,.1,.05), vec3(.2,.3,.1),  vec3(0),        vec3(0)),        // sprouting
    Demo( .4,   .007, .001,  .5, 4, vec3(.5,0,0),    vec3(.1,.2,.0),  vec3(.3,.5,.4), vec3(.5,.2,1)),  // celestial
    Demo( .01,  .01,  .002,  .5, 3, vec3(.3,.2,.1),  vec3(.2,.3,.1),  vec3(.2,.5,.3), vec3(0)),        // arboreal
    Demo(1.,    .005, .001,  .9, 4, vec3(.2,.2,.5),  vec3(.3,.3,.5),  vec3(.4,.4,.5), vec3(.5)),       // climactic
    Demo( .2,   .01,  .005, 1.5, 2, vec3(.05,.1,.2), vec3(.1,.2,.05), vec3(0),        vec3(0)),        // two-color slow
    Demo( .05,  .007, .01,  1.5, 2, vec3(0,.4,0),    vec3(.4,0,.2),   vec3(0),        vec3(0)),        // two-color fast
    Demo( .05,  .007, .003,  .3, 1, vec3(.6,.8,1),   vec3(0),         vec3(0),        vec3(0))         // ice
);

void initGlobals(vec3 res, vec4 state)
{
    Demo demo = DEMOS[int(state.w) % NUM_DEMOS];
    Demo nextDemo = DEMOS[int(state.w + 1.) % NUM_DEMOS];

    float used = fract(state.w) > .9 ? min(demo.used, nextDemo.used) : demo.used;
    g_MaxParticles = int(res.x * res.y * used) / NUM_PARTICLE_DATA_TYPES;

    float transition = smoothstep(.9, 1., fract(state.w));
    g_NumColors = demo.colors;
    g_ParticleSize = mix(demo.size, nextDemo.size, transition);
    g_ParticleSpeed = mix(demo.speed, nextDemo.speed, transition);
    g_ParticleTurn = mix(demo.turn, nextDemo.turn, transition);
    g_ColorR = mix(demo.colorR, nextDemo.colorR, transition);
    g_ColorG = mix(demo.colorG, nextDemo.colorG, transition);
    g_ColorB = mix(demo.colorB, nextDemo.colorB, transition);
    g_ColorA = mix(demo.colorA, nextDemo.colorA, transition);
}

