// ---------------------------------------------------------------------------------------
// Computes particle positions and velocities
// ---------------------------------------------------------------------------------------

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.y, sc.x, -sc.x, sc.y);
}

void mainImage( out vec4 fragColor, vec2 fragCoord ){
    ivec2 iFragCoord = ivec2(fragCoord);
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    initGlobals(iResolution, state);

    int index = iFragCoord.x + iFragCoord.y*int(iResolution.x);
    int id = index / NUM_PARTICLE_DATA_TYPES;
    int dataType = index - id * NUM_PARTICLE_DATA_TYPES;    

    fxParticle data = fxGetParticle(id);

    if (iFrame == 0 || state.x < 0. || id >= g_MaxParticles)
    {
        vec3 h = hash3(uvec3(iFragCoord.x*iFragCoord.y, iFragCoord.y, iFrame*iFragCoord.x));
        vec3 h2 = hash3(uvec3(iFragCoord.y, iFrame + int(iDate.w), iFragCoord.x));
        data.pos = vec2(iResolution.x / iResolution.y, 1) * (h2.xy * 2. - 1.);
        data.vel = g_ParticleSpeed*normalize((vec2(h.x, h.y) - .5));
    }
    else
    {
        // probe the density texture ahead of us, to the left and to the right
        vec2 p = data.pos * .5 * vec2(iResolution.y / iResolution.x, 1) + .5;
        vec4 fwdC = texture(iChannel2, p + data.vel * 2.);
        vec2 leftV = data.vel * rot2(g_ParticleTurn);
        vec4 leftC = texture(iChannel2, p + leftV * 3.);
        vec2 rightV = data.vel * rot2(-g_ParticleTurn);
        vec4 rightC = texture(iChannel2, p + rightV * 2.);

        vec4 color = colorFromId(id);
        color = color * 2. - 1.;

        float fwd = dot(color, fwdC);
        float left = dot(color, leftC);
        float right = dot(color, rightC);

        // turn towards our color and away from other colors
        if (fwd < left || fwd < right)
        {
            if (left < right)
            {
                data.vel = mix(data.vel, rightV, 0.3*right / (fwd + right));
            }
            else
            {
                data.vel = mix(data.vel, leftV, 0.3*left / (fwd + left));
            }
        }

        // always move at g_ParticleSpeed
        if (length(data.vel) > 1e-10)
        {
            data.vel = normalize(data.vel) * g_ParticleSpeed;
        }
        else
        {

            vec3 h = hash3(uvec3(iFragCoord.x*iFragCoord.y, iFragCoord.y, iFrame*iFragCoord.x));
            data.vel = g_ParticleSpeed*normalize((vec2(h.x, h.y) - .5));
        }

        // integrate position
        data.pos = data.pos + data.vel;

        // boundary
        float distToScene = distanceFromWalls(data.pos, iResolution, iTime);
        float distToSceneOld = distanceFromWalls(data.pos, iResolution, iTime - iTimeDelta);
        vec2 distNormal = getNormalFromWalls(data.pos, iResolution, iTime);

        if (distToScene < 0.001)
        {
            data.pos = clamp(data.pos, vec2(-iResolution.x / iResolution.y, -1.0), vec2(iResolution.x / iResolution.y, 1.0));
            float dp = dot(data.vel, distNormal);
            if (dp < 0.)
            {
                data.vel = reflect(data.vel, distNormal);
            }
        }

    }


    fragColor = fxSaveParticle(data, dataType);
}

