// ---------------------------------------------------------------------------------------
// Particle render
// ---------------------------------------------------------------------------------------

void renderParticle(in fxParticle p, int id, in vec2 fragCoord, inout vec4 fragColor)
{   
    vec2 closest;
    p.pos.x *= iResolution.y / iResolution.x;
    float dist = linePointDist2(p.pos, p.pos - 1.5 * p.vel, fragCoord, iResolution, closest);
    int maxParticles = min(int(iResolution.x * iResolution.y) / NUM_PARTICLE_DATA_TYPES, g_MaxParticles);

    vec4 color = colorFromId(id) *.03;
    fragColor += color * max(0.0, g_ParticleSize - sqrt(dist)) / g_ParticleSize;
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    initGlobals(iResolution, state);
    fragColor = vec4(0.0);
  	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.xy;

    // diffuse colors
    for (int x = -1; x <= 1; ++x)
    for (int y = -1; y <= 1; ++y)
    {
        vec4 n = texelFetch(iChannel2, ivec2(fragCoord) + ivec2(x, y), 0);
        fragColor += n / (1.08 + 100.*dot(vec2(x, y), vec2(x, y)));
    }

    ivec4 ids = fxGetClosest(ivec2(fragCoord));

    // draw the particles
    for(int i = 0; i < 4; i++)
    {
        int id = ids[i];
        if (id < 0 || id >= g_MaxParticles) continue;
        fxParticle particle = fxGetParticle(id);

        renderParticle(particle, id, p, fragColor);
    }

    // reset
    if (iFrame == 0 || state.x < 0. ||
        (iMouse.z > 0. && distance(fragCoord, iMouse.xy) < 0.1*iResolution.y))
    {
        fragColor = vec4(0);
    }
}

