# define pi 3.141592653589793238462643383279502

mat2 rotateMatrix(float theta)
{
    return mat2(cos(theta),-sin(theta),
                sin(theta),cos(theta));
}

float sdTorus(vec3 p, float r1, float r2)
{
    float d = length(p.xy)-r1;
    return length(vec2(d,p.z))-r2;
}

float sdCylinder(vec3 p, float r, float h)
{
    float d = length(p.xy)-r;
    vec2 w = vec2(d, abs(p.z)-h);
    return min(max(w.x,w.y), 0.0) + length(max(w,0.0));
}

float sdf(vec3 p) 
{
    p.xz *= rotateMatrix(iTime);
    p.xy *= rotateMatrix(iTime);

    // Symmetries
    p = abs(p);
    if(p.x>p.y) p.xy=p.yx;
    if(p.y>p.z) p.yz=p.zy;
    if(p.x>p.y) p.xy=p.yx;

    float cy = sdCylinder(p,0.3,3.0);
    float t = sdTorus(p.zyx,3.0,0.5);

    return min(cy,t);
 }

float rayMarching(vec3 raySource, vec3 direction)
{
    float d = 0.0;
    for (int i=0; i<100; i++)
    {
        float len = sdf(raySource+direction*d);
        if (len<0.001 || d>30.0) break;
        d += len;
    }
    return d;
}

vec3 getNormal(vec3 p)
{
    vec2 e = vec2(0.0001,0);
    float d = sdf(p);
    float pdx=sdf(p+e.xyy)-d;
    float pdy=sdf(p+e.yxy)-d;
    float pdz=sdf(p+e.yyx)-d;
    return normalize(vec3(pdx,pdy,pdz));

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y * 4.0;

    vec3 col = vec3(0.75);
    vec3 raySource = vec3(0,0,20);

    vec3 screen = vec3(uv,1.0);

    vec3 direction = normalize(screen-raySource);

    float d = rayMarching(raySource, direction );
    if(d<30.0)
    {
        vec3 p = raySource+direction*d;
        vec3 N = getNormal(p);
        vec3 light = vec3(5,0,5);
        // light.xz *= rotateMatrix(iTime);

        vec3 L = normalize(light-p);

        float diffuse = dot(N,L);
        // Lambert
        // diffuse = max(0,diffuse);
        // Half Lambert
        diffuse = diffuse * 0.5 + 0.5;


        // Phong
        vec3 R = normalize(2.0*N*dot(N,L)-L);
        vec3 V = -direction;
        // vec3 specular = 0.1*vec3(1)*pow(dot(R,V),8.0);

        // Blinn
        vec3 H = normalize(V+L);
        vec3 specular = 0.9*vec3(1)*pow(dot(H,N),5.0);


        vec3 ambient = vec3(0.1,0.1,0.3)*1.5;

        col = vec3(1,0,1);
        col = ambient + 0.5*col*diffuse + specular;
    }

    // Time varying pixel color
    // col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Parabolic Graph Drawing, mla, 2022
//
// Drawing a y = f(x) graph by finding the distance from (a,b) to the tangent
// at (a,f(a)) is simple and effective for graphs that don't bend too sharply,
// but if they do we get ugly artefacts at the bends. Using a quadratic
// approximation to the curve at (a,f(a) works better, though it involves
// solving a cubic to find the distance to the approximating parabola.
//
// The cubic solver is analytic and uses ideas from other solvers, particularly
// by @iq and @spalmer (as well as papers by Jim Blinn & others). The solver
// works pretty well, though it is improved, particularly on devices with 
// low-quality trig functions, by one round of Newton-Raphson (this does have
// the potential for making the root accuracy worse, but most of the time
// it's an improvement).
//
// I haven't tried to deal with discontinuous or linear functions and there is
// a point at which the parabolic approximation has artefacts (this graph is
// about at the limit in fact).
//
// <mouse>: set the position of the fitting parabola (and interpolation
//          points) that is shown.
// 'x': use linear fitting
// 'a': SSAA
//
////////////////////////////////////////////////////////////////////////////////


int curve = 1;
float F(float x, float t) {
  switch (curve) {
  case 0: return 1.0/sin(x+t);
  case 1: return 4.0*cos(0.3*(x+t))*sin(10.0*cos(x+0.382*t)+t); // The curve
  case 2: return 8.0*abs(fract(0.43*(x+t))-0.5)-2.0; // Triangle wave from @spalmer
  return 0.0;
  }
}

float scale = 6.0;
float linewidth = 0.02;

// Plots a function of x based on three consecutive values.
// This is basically (y-f(x))/|(f'(x),1)|, using an approximate derivative.
float graphdist(vec2 p,float eps, float f0, float f1, float f2){
  assert(f1 == 0.0);
  vec2 base = vec2(0), dir = vec2(2.0*eps,f2-f0);
  return line(p,base,dir);
}

// Match a circle - nice, except there are serious artefacts where p is close
// to the other side of the circle.
float graphdist0(vec2 p,float eps, float f0, float f1, float f2){
  vec2 c0 = vec2(-eps,f0), c2 = vec2(eps,f2);
  vec3 circle = cross(vec3(c0,dot(c0,c0)), vec3(c2,dot(c2,c2)));
  circle /= length(circle.xy);
  return abs(dot(circle.xy,p)+circle.z*dot(p,p));
}

// sgn is not sign
float sgn(float p) { return p < 0.0 ? -1.0 : 1.0; }
float cuberoot(float x) { return sgn(x)*pow(abs(x),1.0/3.0); }

// Solve the reduced cubic x+px+q = 0 and return the largest real root.
float solvecubic(float p, float q) {
  p /= 3.0, q /= -2.0;    // Simplifies the algebra
  float h = q*q + p*p*p;  // Discriminant
  float r = sqrt(abs(h));
  if (h >= 0.0) {
    float u = q+sgn(q)*r; // Largest magnitude root
    float t = cuberoot(u);
    return t - p/t;
  } else {
    // Find real part of 'principal' cube root of -q+ir
    float theta = atan(r,q);               // 0 <= theta < PI since r >= 0 - could use -r
    float k = 2.0*sqrt(abs(p));
    //return k*cos((theta+vec3(0,2,4)*PI)/3.0); // All roots
    return k*cos(theta/3.0); // Largest root wanted here
  }
}

// sdParabola based on a function by @iq
float sdParabola(vec2 pos, in float k ) {
  pos.x = abs(pos.x); // Ensure largest root is nearest
  float ik = 1.0/k;
  float p = -ik*(pos.y - 0.5*ik);
  float q = -0.5*ik*ik*pos.x;
  float x = solvecubic(p,q);
  for (int i = 0; i < 1; i++) {
    // A quick polishing for the root. This makes hardly any difference in
    // this shader, except very slightly when the graph is nearly linear.
    float fx = q+x*(p+x*x);
    float dx = p+3.0*x*x;
    x -= fx/dx;
  } 
  return length(pos-vec2(x,k*x*x));
}

float graphdist2(vec2 p, float eps, float y0, float y1, float y2) { 
  assert(y1 == 0.0);
  if (key(CHAR_X) || abs(y0+y2)<1e-4) return graphdist(p,eps,y0,y1,y2);
  // Parabolic approximation: y = Ax+ Bx
  // so y0 = A.eps-B.eps, y2 = A.eps+B.eps
  float A = 0.5*(y2+y0)/(eps*eps);
  float B = 0.5*(y2-y0)/eps; // For reference
  // Change of variable
  float k = 0.5*(y2-y0)/(y2+y0);
  float d = abs(sdParabola(p+vec2(eps*k,0.25*(y2-y0)*k),A));
  return d;
}

vec2 map(vec2 coords) {
  vec2 uv = (2.0*coords - iResolution.xy)/iResolution.y;
  uv *= scale;
  return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float t = iTime;
  vec2 uv = map(fragCoord);
  vec2 mouse = vec2(0);
  bool domouse = true; //iMouse.x > 10.0;
  if (iMouse.x > 0.0) mouse = map(iMouse.xy);
  float x = uv.x, y = uv.y;
  float px = fwidth(x);
  assert(px >0.);
  float a = mouse.x;

  //col = mix(vec3(0.1),col,smoothstep(0.0,px,abs(y)-0.25*lwidth)); // Axes
  float eps = 4.0*scale/iResolution.y; // Power of 2 may be better
  vec3 aacol = vec3(0);
  int N = 2; // SSAA
  if (key(CHAR_A)) N = 1;
  for (int i = 0; i < N; i++) {
    for (int j = 0; j < N; j++) {
      vec3 col = vec3(0.5);
      vec2 uv = map(fragCoord+vec2(i,j)/float(N)); // Remap fragCoord
      float x = uv.x, y = uv.y;
      float y0 = F(x-eps,t), y1 = F(x,t), y2 = F(x+eps,t);
      float d = graphdist2(vec2(0,y-y1),eps,y0-y1,0.0,y2-y1);
      //col *= 0.5+0.5*cos(4.0*PI*d);
      col = mix(vec3(0),col,smoothstep(0.0,px,d-1.5*linewidth));
      col = mix(vec3(1,0,0),col,smoothstep(0.0,px,d-1.5*linewidth));
      col = mix(vec3(1),col,smoothstep(0.0,px,d));
      if (domouse) {
        float y0 = F(a-eps,t), y1 = F(a,t), y2 = F(a+eps,t);
        vec2 p = vec2(x-a,y-y1);
        float d = graphdist2(p,eps,y0-y1,y1-y1,y2-y1);
        col = mix(vec3(0.0),col,smoothstep(0.0,px,d-0.5*linewidth));
        d = 1e8;
        d = min(d,distance(p,vec2(-eps,y0-y1)));
        d = min(d,distance(p,vec2(0)));
        d = min(d,distance(p,vec2(eps,y2-y1)));
        col = mix(vec3(1),col,smoothstep(0.0,px,d-1.5*linewidth));
      }
      aacol += col;
    }
  }
  aacol /= float(N*N);
  aacol = pow(aacol,vec3(0.4545));
  if (alert) aacol.g = 1.0;
  fragColor = vec4(aacol,1.0);
}

