// Credit: https://www.shadertoy.com/view/DsBGzy by sh1boot
vec3 hsv2rgb(vec3 hsv) 
{
    vec3 h3 = mod(6.0 * hsv.x + vec3(5.0, 3.0, 1.0), 6.0);
    h3 = min(h3, 4.0 - h3);
    h3 = clamp(h3, 0.0, 1.0);
    return hsv.z - hsv.z * hsv.y * h3;
}

