// Cached data buffer.
// Things that only need to be calculated once will be stored here.

void getSamplingCoords(out ivec3 coords[SamplingOrderCount], int maxIndex)
{
    // coordinate:
    // x, y: position
    // z:    layer

    // NOTE: SamplingOrderCount and maxIndex are evaluated
    // separately in hopes that the compiler
    // will unroll the for loops
    // and break early given one of the
    // arguments is a constant.
    // If we combine the two limits,
    // it might be dumb and assume that
    // the breaking condition is non-constant
    // and therefore,
    // there is no definite upper bound
    // to the number of iterations,
    // thus preventing the loop from being unrolled.

    int iCoord = 0;
    if(iCoord>=SamplingOrderCount) return;
    if(iCoord>=maxIndex) return;

    coords[iCoord++] = ivec3(0);
    if(iCoord>=SamplingOrderCount) return;
    if(iCoord>=maxIndex) return;

    for(int iLayer = 1; ; iLayer++)
    {
        // Add primaries (left, right, down, up).
        {
            ivec3 swizzler = ivec3(-iLayer, iLayer, 0);

            coords[iCoord++] = ivec3(swizzler.xzy); // left
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yzy); // right
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zxy); // bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zyy); // top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }

        // Add coordinates between corners and primaries.
        for(int iMinor = 1; iMinor < iLayer; iMinor++)
        {
            // iLayer = length of longer (major) arm.
            // iMinor = length of shorter (minor) arm.

            ivec4 swizzler = ivec4(iLayer, iMinor, -iLayer, -iMinor);

            coords[iCoord++] = ivec3(swizzler.xyx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.xwx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zyx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zwx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;

            coords[iCoord++] = ivec3(swizzler.yxx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.wxx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yzx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.wzx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }

        // Add corners.
        {
            ivec2 swizzler = ivec2(-iLayer, iLayer);

            coords[iCoord++] = ivec3(swizzler.xxy); // left bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.xyy); // left top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yxy); // right bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yyy); // right top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }
    }
}


void UpdateSamplingOrderData( out vec4 fragColor, in ivec2 iCoord )
{
    switch(iFrame)
    {
        case SamplingOrderFrameImprecise:
        {
            ivec3 coords[SamplingOrderCount];
            int coordComputeCount = min(SamplingOrderCount, iCoord.x+1);
            getSamplingCoords(coords, coordComputeCount);
            fragColor.xyz = vec3(coords[coordComputeCount-1]);
            fragColor.w = 1.0;
            return;
        }
        case SamplingOrderFramePrecise:
        {

int n = min(SamplingOrderCount, iCoord.x+1);
int maxN = min(SamplingOrderCount, int(float(n)*1.5));
ivec3 closestNCoords[SamplingOrderCount];
float closestNDistances[SamplingOrderCount];
int closestNIndices[SamplingOrderCount];
int closestNCount = 0;

// TODO: Skip more comparisons by better exploiting
//       the fact the list is already
//       semi-sorted.

for(int thisIndex = 0; thisIndex < maxN; thisIndex++)
{
    ivec3 thisCoord = GetSamplingOrderCoord(thisIndex, DATA);
    float thisDist = GetSamplingDistance(thisIndex, DATA);
    if(closestNCount < n)
    {
        // Add it to the list.
        closestNCoords[closestNCount] = thisCoord;
        closestNDistances[closestNCount] = thisDist;
        closestNIndices[closestNCount] = thisIndex;
        closestNCount++;
    }
    else
    {
        // Replace the furthest one in the list if it is better.

        int elementToReplace = 0;
        int furthestIndex = closestNIndices[0];
        float furthestDistance = closestNDistances[0];
        {
            for(int j = 1; j < n; j++)
            {
                if(furthestDistance < closestNDistances[j])
                {
                    elementToReplace = j;
                    furthestIndex = closestNIndices[j];
                    furthestDistance = closestNDistances[j];
                }
                else if(furthestDistance == closestNDistances[j])
                {
                    // In the case where distances are identical,
                    // use their position in the original list instead.
                    // This ensures that all elements have
                    // a unique rank and thus are kept.
                    if(furthestIndex < closestNIndices[j])
                    {
                        elementToReplace = j;
                        furthestIndex = closestNIndices[j];
                        furthestDistance = closestNDistances[j];
                    }
                }
            }
        }

        if(thisDist < furthestDistance)
        {   
            closestNCoords[elementToReplace] = thisCoord;
            closestNDistances[elementToReplace] = thisDist;
            closestNIndices[elementToReplace] = thisIndex;
        }
        else if(thisDist == furthestDistance)
        {
            // In the case where distances are identical,
            // use their position in the original list instead.
            // This ensures that all elements have
            // a unique rank and thus are kept.
            if(thisIndex < furthestIndex)
            {
                closestNCoords[elementToReplace] = thisCoord;
                closestNDistances[elementToReplace] = thisDist;
                closestNIndices[elementToReplace] = thisIndex;
            }
        }
    }
}

// Our list contains the N closest coordinates.
// The furthest in this list is the Nth closest element.
// This will be the coordinate for this fragment.
int resultElement = 0;
{
    int furthestIndex = closestNIndices[0];
    float furthestDistance = closestNDistances[0];
    {
        for(int j = 1; j < n; j++)
        {
            if(furthestDistance < closestNDistances[j])
            {
                resultElement = j;
                furthestIndex = closestNIndices[j];
                furthestDistance = closestNDistances[j];
            }
            else if(furthestDistance == closestNDistances[j])
            {
                // In the case where distances are identical,
                // use their position in the original list instead.
                // This ensures that all elements have
                // a unique rank and thus are kept.
                if(furthestIndex < closestNIndices[j])
                {
                    resultElement = j;
                    furthestIndex = closestNIndices[j];
                    furthestDistance = closestNDistances[j];
                }
            }
        }
    }
}


            fragColor.xyz = vec3(closestNCoords[resultElement]);
            fragColor.w = 1.0;
            return;
        }
    }

    fragColor = texelFetch(DATA, iCoord, 0);
}

float MinimumDistanceBetweenSquares(in ivec2 p1, in ivec2 p2)
{
    vec2 p1Center = vec2(p1) + vec2(0.5);
    vec2 p2Center = vec2(p2) + vec2(0.5);
    vec2 p1ToP2 = p2Center - p1Center;
    vec2 offset = clamp(p1ToP2, vec2(-1.0), vec2(1.0));
    return lengthSqr(p1ToP2 - offset);
}

void UpdateSamplingDistanceData( out vec4 fragColor, in ivec2 iCoord )
{
    switch(iFrame)
    {
        case SamplingDistanceFrameImprecise:
        case SamplingDistanceFramePrecise:
        {
            ivec2 neighbor = GetSamplingOrderCoord(iCoord.x, DATA).xy;
            ivec2 me = ivec2(0);
            fragColor = vec4(0);
            fragColor.x = MinimumDistanceBetweenSquares(me, neighbor);
            return;
        }
    }

    fragColor = texelFetch(DATA, iCoord, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = floorToInt(fragCoord);

    switch(iCoord.y)
    {
        case SamplingOrderRow: UpdateSamplingOrderData(fragColor, iCoord); return;
        case SamplingDistanceRow: UpdateSamplingDistanceData(fragColor, iCoord); return;
    }

    fragColor = texelFetch(DATA, iCoord, 0);
}

