// Cached data buffer.
// Things that only need to be calculated once will be stored here.

void getSamplingCoords(out ivec3 coords[SamplingOrderCount], int maxIndex)
{
    // coordinate:
    // x, y: position
    // z:    layer

    // NOTE: SamplingOrderCount and maxIndex are evaluated
    // separately in hopes that the compiler
    // will unroll the for loops
    // and break early given one of the
    // arguments is a constant.
    // If we combine the two limits,
    // it might be dumb and assume that
    // the breaking condition is non-constant
    // and therefore,
    // there is no definite upper bound
    // to the number of iterations,
    // thus preventing the loop from being unrolled.

    int iCoord = 0;
    if(iCoord>=SamplingOrderCount) return;
    if(iCoord>=maxIndex) return;

    coords[iCoord++] = ivec3(0);
    if(iCoord>=SamplingOrderCount) return;
    if(iCoord>=maxIndex) return;

    for(int iLayer = 1; ; iLayer++)
    {
        // Add primaries (left, right, down, up).
        {
            ivec3 swizzler = ivec3(-iLayer, iLayer, 0);

            coords[iCoord++] = ivec3(swizzler.xzy); // left
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yzy); // right
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zxy); // bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zyy); // top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }

        // Add coordinates between corners and primaries.
        for(int iMinor = 1; iMinor < iLayer; iMinor++)
        {
            // iLayer = length of longer (major) arm.
            // iMinor = length of shorter (minor) arm.

            ivec4 swizzler = ivec4(iLayer, iMinor, -iLayer, -iMinor);

            coords[iCoord++] = ivec3(swizzler.xyx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.xwx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zyx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.zwx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;

            coords[iCoord++] = ivec3(swizzler.yxx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.wxx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yzx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.wzx);
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }

        // Add corners.
        {
            ivec2 swizzler = ivec2(-iLayer, iLayer);

            coords[iCoord++] = ivec3(swizzler.xxy); // left bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.xyy); // left top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yxy); // right bottom
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
            coords[iCoord++] = ivec3(swizzler.yyy); // right top
            if(iCoord>=SamplingOrderCount) return;
            if(iCoord>=maxIndex) return;
        }
    }
}

void UpdateSamplingOrderData( out vec4 fragColor, in ivec2 iCoord )
{
    if(iFrame != 0)
    {
        fragColor = texelFetch(iChannel0, iCoord, 0);
        return;
    }

    ivec3 coords[SamplingOrderCount];
    int coordComputeCount = min(SamplingOrderCount, iCoord.x+1);
    getSamplingCoords(coords, coordComputeCount);
    fragColor.xyz = vec3(coords[coordComputeCount-1]);
    fragColor.w = 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = floorToInt(fragCoord);

    switch(iCoord.y)
    {
        case SamplingOrderRow:
        {
            UpdateSamplingOrderData(fragColor, iCoord);
            return;
        }
    }

    fragColor = texelFetch(iChannel0, iCoord, 0);
}

