// Returns the minimum and maximum distance from
// a given coordinate to a square whose minimum
// is coord, and whose maximum is coord+(1,1).
vec2 getMinMaxDistSqr(vec2 point, ivec2 coord)
{
    vec2 squareCenter = vec2(coord) + vec2(0.5);

    vec2 dirToCenter = squareCenter - point;

    vec2 minOffset = clamp(dirToCenter, vec2(-0.5), vec2(0.5)) * -1.0;

    vec2 maxOffset = vec2
    (
        dirToCenter.x < 0. ? -0.5 : 0.5,
        dirToCenter.y < 0. ? -0.5 : 0.5
    );

    vec2 distances = vec2
    (
        lengthSqr(dirToCenter + minOffset),
        lengthSqr(dirToCenter + maxOffset)
    );

    return distances;
}


